import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group';
import Validator from 'react-forms-validator';
// import { Link } from 'react-router-dom';
import callApi from './../../utils/apiCaller';
import * as config from './../../constants/config';
import Button from '@material-ui/core/Button';
import Swal from 'sweetalert2';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import ListItemText from '@material-ui/core/ListItemText';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';
import Chip from '@material-ui/core/Chip';

const MenuProps = {
	PaperProps: {
	  style: {
		maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
		width: 250,
	  },
	},
  };

const styles = theme => ({
	root: {
	  display: 'flex',
	  flexWrap: 'wrap',
	},
	formControl: {
	  margin: theme.spacing.unit,
	  minWidth: 120,
	  maxWidth: 300,
	},
	chips: {
	  display: 'flex',
	  flexWrap: 'wrap',
	},
	chip: {
	  margin: theme.spacing.unit / 4,
	},
	noLabel: {
	  marginTop: theme.spacing.unit * 3,
	},
  });

  const ITEM_HEIGHT = 48;
  const ITEM_PADDING_TOP = 8;
const names = [
	'Oliver Hansen',
	'Van Henry',
	'April Tucker',
	'Ralph Hubbard',
	'Omar Alexander',
	'Carlos Abbott',
	'Miriam Wagner',
	'Bradley Wilkerson',
	'Virginia Andrews',
	'Kelly Snyder',
  ];
class Permissions extends Component {

    constructor (props) {
        super(props)
        this.state = {
            old_password: '',
            new_password: '',
			confirm_password: '',
			checkbox: '',
			name: [],
            isFormValidationErrors : true,
			submitted: false,
        }
	}
	handleChange = event => {
		this.setState({ name: event.target.value });
	  };



    isValidationError = (flag) => {
		this.setState({isFormValidationErrors:flag});
   	}

    onChangeForm = (event) => {
        var target = event.target;
		var name = target.name;
		var value = target.type === 'checkbox'? target.checked : target.value;
		this.setState({
			[name]: target.value
		});
	}

	// handleChange = (e) => {
	// 	var options = e.target.options;
	// 	var value = [];
	// 	for (var i = 0, l = options.length; i < l; i++) {
	// 	  if (options[i].selected) {
	// 		value.push(options[i].value);
	// 	  }
	// 	}
	// 	console.log(value);
	//   }


    handleFormSubmit = (event) => {
		event.preventDefault();
		console.log(this.state.checkbox); return;
		this.setState({ submitted:true });
		var {old_password, new_password, confirm_password} = this.state;
		var data = { 
			old_password: old_password,
			new_password: new_password,
			confirm_password: confirm_password
		};
		let { isFormValidationErrors } = this.state;
        if ( !isFormValidationErrors ){

			callApi('POST', config.API_URL + "change-password", data).then( res => {
                console.log(res);
                if(res){
                    if( res.data.status ){
                        Swal(' Good job!', res.data.message, 'success')
                    } else {
                        Swal('Oops...', res.data.message, 'error')
                    }
                }
            });
        } else {
            Swal('Oops...', 'Something went wrong!', 'error')

        }
    }

    render() {

        return (
            <React.Fragment>
                <CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
                    <div className="grid simple">
                    <div className="grid-body no-border">


                        <form noValidate onSubmit={this.handleFormSubmit}>
							<div className="col-lg-6 col-md-6">


							<Select
								multiple
								value={this.state.name}
								onChange={this.handleChange}
								input={<Input id="select-multiple" />}
								MenuProps={MenuProps}
							>
							{names.map(name => (
								<MenuItem key={name} value={name} style={getStyles(name, this)}>
									{name}
								</MenuItem>
								))}
							</Select>


								<div className="form-group">
									<label>Users</label>
									<select value={['4']} name="checkbox"  onChange={this.handleChange}  multiple >
										<option value="" disabled selected>Choose your country</option>
										<option value="quan">USA</option>
										<option value="van">Germany</option>
										<option value="duong">France</option>
										<option value="4">Poland</option>
										<option value="5">Japan</option>
									</select>

								</div>


							</div>
							<div className="col-lg-12 col-md-12">
								<Button type="submit" variant="contained" color="primary">Save</Button>
							</div>
						</form>

                    </div>
                </div>


                </CSSTransitionGroup>
            </React.Fragment>
        );
    }
}

export default Permissions;
