import React, { Component } from 'react';
import UsersList from './../../components/Users/UsersList';
import UserSpec from './../../components/Users/UserSpec';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { actFetchUsersRequest, actDeleteUserRequest } from './../../actions/index';
import { Redirect } from 'react-router-dom';
// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import matchSorter from 'match-sorter'


class UsersPage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			image: '',
			loggedOut: false
		}
		this.onDelete = this.onDelete.bind(this);
	}



	componentWillReceiveProps(nextprops) {
        if(!nextprops.authentication.loggedOut){
			this.setState({
				loggedOut: nextprops.authentication.loggedOut
			});

		}

    }
	componentWillMount(){
		this.props.getUsers();
	}
	onDelete (id) {
		if (window.confirm('Are you sure you wish to delete this item?')){
			this.props.onDeleteUser(id)
		}

	}

	render() {
		if(this.state.loggedOut){
			return <Redirect to={{ pathname: "/"}}/>;
		}
		if (this.props.users !== null) {
			var {users} = this.props.users;
		}

		return (

			<div className="col-lg-12 col-sm-12 col-xs-12 col-md-12">



				<Link to="/users/add" className="btn btn-primary">
					Add
				</Link>
				{ this.showUser(users) }

			{/* // 	<br/><br/>
			// 	<UsersList>
            //         { this.showUser(users) }
			// 	</UsersList> */}
			</div>
		);
	} // end render

	showUser (users) {
		var result = null;
		if( users ){
			if ( users && typeof users !== 'undefined' && users.length > 0) {
				return <ReactTable
							data={users}
							filterable
							defaultFilterMethod={(filter, row) =>
								String(row[filter.id]) === filter.value}
							columns={[
								{
									Header: undefined,

									columns: [
										{
											Header: "ID",
											width: 80,
											id: "id",
											accessor: d => d.id,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["id"] }),
											filterAll: true
										},

										{
											Header: "UserName",
											id: "username",
											accessor: d => d.username,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["username"] }),
											filterAll: true
										},
										{
											Header: "firstname",
											id: "firstname",
											accessor: d => d.firstname,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["firstname"] }),
											filterAll: true
										},
										{
											Header: "lastname",
											id: "lastname",
											accessor: d => d.lastname,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["lastname"] }),
											filterAll: true
										},
										{
											Header: "email",
											id: "email",
											accessor: d => d.email,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["email"] }),
											filterAll: true
										},
										{
											Header: "address",
											id: "address",
											accessor: d => d.address,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["address"] }),
											filterAll: true
										},
										{
											Header: "phone",
											id: "phone",
											accessor: d => d.phone,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["phone"] }),
											filterAll: true
										},
										{
											Header: "birthday",
											id: "birthday",
											accessor: d => d.birthday,
											filterMethod: (filter, rows) =>
											  matchSorter(rows, filter.value, { keys: ["birthday"] }),
											filterAll: true
										},
										{
											Header: "Action",
											accessor: "id",
											Cell: row => (
												<div>
												  	<Link to={`users/${row.value}/edit`} className="btn btn-success margin-right-10">
														Edit
													</Link>
													<button type="button" className="btn btn-danger" onClick={ () => this.onDelete(row.value)}>Delete</button>
												</div>
											  )
										}
									]
								}


						]}
					defaultPageSize={10}
					className="-striped -highlight"
					/>
			}
		}
		return result;
	}

}

const mapStateToProps = state => {
	return {
		authentication: state.authentication,
		users: state.users
	}
}

const mapDispatchToProps = (dispatch, props) => {
	return {
		getUsers : () => {
			dispatch(actFetchUsersRequest());
		},
		onDeleteUser : (id) => {
			dispatch(actDeleteUserRequest(id));
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersPage);
