import React, { Component } from 'react';
import * as config from '../../constants/config';

class InvoiceDetails extends Component {
    currency = (n, currency) => {
		if(n && n !== ''){
			return currency + n.replace(/./g, function(c, i, a) {
				return i > 0 && c !== "." && (a.length - i) % 3 === 0 ? "," + c : c;
			});
		}
	};
    render() {
        var {order} = this.props;

        return (

                <div className="card-body">
                    <div className="row mb-4">
                        <div className="col-sm-6">
                            <h6 className="mb-3"></h6>
                            <div>
                                BÃ¡c sá»¹: <strong>{order.user.firstname + ' ' + order.user.lastname} ({order.category.title})</strong>
                            </div>
                            <div>Email: {order.user.email}</div>
                            <div>Äiá»n thoáº¡i: {order.user.phone}</div>
                        </div>
                        <div className="col-sm-6">
                            <h6 className="mb-3"></h6>
                            <div>
                                Bá»nh nhÃ¢n: <strong>{order.customer.firstname + ' ' + order.customer.lastname} </strong>
                            </div>
                            <div>Email: {order.customer.email}</div>
                            <div>Äiá»n thoáº¡i: {order.customer.phone}</div>
                            <div>Äá»a chá»: {order.customer.address}</div>
                            <div>Giá»i tÃ­nh: {order.customer.gender === config.GENDER_MALE ? 'Ná»¯' : 'Nam'}</div>
                        </div>
                    </div>
                    <div className="table-responsive-sm">
                        <table className="table table-striped">
                        <thead>
                            <tr>
                                <th className="center">#</th>
                                <th>Dá»ch vá»¥</th>
                                <th className="right">GiÃ¡</th>
                                <th className="center">Sá» lÆ°á»£ng</th>
                                <th className="right">Tá»ng tiá»n</th>

                            </tr>
                        </thead>
                            <tbody>

                                {this.showData(order.order_detail)}

                            </tbody>
                        </table>
                    </div>
                    <div className="row">
                        <div className="col-lg-4 col-sm-5">
                        </div>
                        <div className="col-lg-4 col-sm-5 ml-auto float-right">
                        <table className="table table-clear">
                            <tbody>
                                <tr>
                                    <td className="left">
                                    <strong>Subtotal</strong>
                                    </td>
                                    <td className="right">{this.currency(order.total)}</td>
                                </tr>

                                <tr>
                                    <td className="left">
                                    <strong>VAT (10%)</strong>
                                    </td>
                                    <td className="right">{order.total/10}</td>
                                </tr>
                                <tr>
                                    <td className="left">
                                    <strong>Total</strong>
                                    </td>
                                    <td className="right">
                                    <strong>{order.total + order.total/10}</strong>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        </div>
                    </div>
                </div>



        );
    }

    showData(order_detail){
        var result = null;
		if(order_detail && typeof order_detail !== 'undefined' && order_detail.length > 0){
			result = order_detail.map((item, index) => {
				return  (<tr key={index}>
                    <td className="center">{index+1}</td>
                    <td className="left strong">{item.product_title}</td>
                    <td className="right">{item.product_price}</td>
                    <td className="left">{item.quantity}</td>
                    <td className="right">{item.totalPriceQuantity}</td>
                </tr>);
			})
			console.log(order_detail);
		}
		return result;

    }
}

export default InvoiceDetails;
