import React, { Component } from 'react';
import { Route, Link } from 'react-router-dom';
import { ASSISTANT, RECRPTIONIST} from './../../constants/config';
import { actLogoutRequest } from './../../actions/index';
import { connect } from "react-redux";

const menu = [
	{
		name: 'Trang chủ',
		to: '/',
		exact: true,
		icon: 'home'
	},
	{
		name: 'Dịch vụ',
		to: '/products',
		exact: false,
		icon: 'layers'
	},
	{
		name: 'Lịch sử dụng dịch vụ',
		to: '/orders',
		exact: false,
		icon: 'card_giftcard'
	},
	{
		name: 'Khách hàng',
		to: '/customers',
		exact: false,
		icon: 'supervisor_account'
	},
	{
		name: 'Lịch sử cuộc gọi',
		to: '/calling',
		exact: false,
		icon: 'phone_in_talk'
	},
	{
		name: 'Tin nhắn/ SMS',
		to: '/sms',
		exact: false,
		icon: 'question_answer'
	},
	{
		name: 'Nhân viên',
		to: '/users',
		exact: false,
		icon: 'account_circle'
	},
];

const menuReceptionist = [
	{
		name: 'Trang chủ',
		to: '/',
		exact: true,
		icon: 'home'
	},
	{
		name: 'Dịch vụ',
		to: '/products',
		exact: false,
		icon: 'layers'
	},
	{
		name: 'Khách hàng',
		to: '/customers',
		exact: false,
		icon: 'supervisor_account'
	},
	{
		name: 'Lịch sử cuộc gọi',
		to: '/calling',
		exact: false,
		icon: 'phone_in_talk'
	},
];

const menuAssistant = [
	{
		name: 'Trang chủ',
		to: '/',
		exact: true,
		icon: 'home'
	},
	{
		name: 'Dịch vụ',
		to: '/products',
		exact: false,
		icon: 'layers'
	},
	{
		name: 'Lịch sử dụng dịch vụ',
		to: '/orders',
		exact: false,
		icon: 'card_giftcard'
	},


];

const MenuLink = ({ label, to, activeOnlyWhenExact, icon}) => {

	return (
		<Route
			path={to}
			exact={activeOnlyWhenExact}
			children={({ match }) => {
				var active = (match && match !== null) ? 'active' : '';
				return (
					<li className={`class-default ${active}`}>
						<Link to={to} className="my-link">
							<i className="material-icons">{icon}</i>
							<span className="title">{label}</span>
						</Link>
					</li>
				)
			}}
		/>
	)
}

class Menu extends Component {

	constructor(props) {
		super(props);
		this.showMenu = this.showMenu.bind(this);
	}
	showMenu(role_id) {
		var result = null;
		var menus = menu;
		if(role_id === RECRPTIONIST){
			menus = menuReceptionist;
		} else if (role_id === ASSISTANT) {
			menus = menuAssistant;
		} 
		if (menus.length > 0) {
			result = menus.map((m, index) => {
				return (<MenuLink label={m.name} to={m.to} activeOnlyWhenExact={m.exact} key={index} icon={m.icon}/>)
			});
		}
		return result;
	}

	onLogout() {
        this.props.onActLogout();
    }

	render() {
		var {role_id} = this.props;
		return (
			<ul>
				{this.showMenu(role_id)}
				<li className="class-default logout-action" onClick={() => this.onLogout()}>
					<a className="my-link" href="/#">
						<i className="material-icons">input</i><span className="title">Đăng xuất</span>
					</a>
				</li>
			</ul>
		);
	}
}



const mapStateToProps = state => {
    return {
        authentication: state.authentication
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        onActLogout: () => {
            dispatch(actLogoutRequest());
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Menu);
