        // var data = { start: this.state.start_date, end: this.state.end_date}

        // this.props.action( data )
    }

    render() {
        var { order_id }  = this.props;
        return (
            <div>
                <h2>Chọn thời gian</h2>
                <p>
                    Lọc bênh nhân, danh mục, thời gian thăm khám
                </p>

                    <div className="col-md-6">
                        <DatePicker
                            className="form-control"
                            placeholderText="VD: 25/10/2018"
                            dateFormat="DD/MM/YYYY"
                            selected={this.state.startDate}
                            selectsStart
                            startDate={this.state.startDate}
                            endDate={this.state.endDate}
                            onChange={this.handleChange}
                        />
                        </div>
                        <div className="col-md-6">
                        <DatePicker
                            className="form-control"
                            placeholderText="VD: 25/10/2018"
                            dateFormat="DD/MM/YYYY"
                            selected={this.state.endDate}
                            selectsEnd
                            startDate={this.state.startDate}
                            endDate={this.state.endDate}
                            onChange={this.handleChangeEnd}
                        />
                        </div>
                        <div className="clearfix"></div><br/>

                <p>
                    <Button 
                        className="btn btn-primary" 
                        variant="contained" 
                        color="secondary" 
                        onClick={ () => { this.onClick(order_id) } }
                        >OK</Button>
                </p>
            </div>
        );
    }
}

export default ModalOrder;
