import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group';
import Button from '@material-ui/core/Button';

// import { Link } from 'react-router-dom';
import * as config from '../../constants/config';
import callApi from '../../utils/apiCaller';
import OrderData from './../../components/Orders/OrderData';
import OrderDataTr from './../../components/Orders/OrderDataTr';
import Cleave from 'cleave.js/react';


class OrderActionPage extends Component {

	constructor(props){
		super(props);
		this.state = {
			service: '',
			customer: '',
			product: '',
			product_quantity: 1,
			customerData: '',
			product_service_id: '',
			categories: '',
			order: '',
			order_detail: '',
			category_title: '',
			date_remain: '10/06/2018'
		};
		this.onSave = this.onSave.bind(this);
		this.onChangeForm = this.onChangeForm.bind(this);
	}

	componentWillMount(){
		var {match} = this.props;
		if(match) {
			var {id, customer_id} = match.params;
			callApi('GET', config.ORDER_URL + id, null).then(res => {
				if (res && res.data.data){
					this.setState({
						order: res.data.data,
						// date_remain: res.data.data.time,
						category_title: res.data.category.title
					});
					var category_id = res.data.data.product_category_id;
					callApi('GET', config.PRODUCT_URL+'category/'+category_id, null).then(res => {
						this.setState({
							categories: res.data.data
						});
					});
				}
			});	
			callApi('GET', config.CUSTOMER_URL + customer_id, null).then(response => {
				if (response ){
					this.setState({
						customer: response.data.data
					});
				}
			});
			if(id){
				this.getOrderDetail(id);
			}
		}
	}

	today = () => {
		var today = new Date();
		var dd = today.getDate();
		var mm = today.getMonth()+1; //January is 0!

		var yyyy = today.getFullYear();
		if(dd<10){
			dd='0'+dd;
		} 
		if(mm<10){
			mm='0'+mm;
		} 
		return mm+'/'+dd+'/'+yyyy;
	}

	getOrderDetail(id){
		callApi('GET', config.ORDER_DETAIL_URL+ "order/" + id, null).then(res => {
			if(res && res.data.status){
				this.setState({
					order_detail: res.data.data,
					time: res.data.data.time
				});
			}
		});
	}

	onChangeForm (event) {
		var target = event.target;
		var name = target.name;
		var value = target.type === 'checkbox'? target.checked:target.value;
		this.setState({
			[name]: value
		});
	}

	onSave (event) {
		event.preventDefault();

		var {history} = this.props;
		var {id, sku, title, price, quantity, unit, is_publish} = this.state;
		var data = {sku: sku, title: title, price: price, quantity: quantity, unit: unit, is_publish: is_publish ? true : false };
		if(id) { //update
			callApi('PUT', config.ORDER_URL + id, data).then( res => {
				history.push("/orders");
			});
		} else { //create
			callApi('POST', config.ORDER_URL, data ).then( res => {
				//C1 // history.push("/orders-list");
				// console.log(res);
				// history.push("/orders");
				//C2: redirect 
				history.goBack();
			});
		}
	}

	handleAddService = (event) => {
		event.preventDefault();
		var {product_service_id, order, product_quantity}  = this.state;
		var data = {order_id: order.id, product_id : product_service_id, quantity: product_quantity}
		if (product_service_id && product_service_id !== ""){
			// if (window.confirm('Are you sure you wish to add this item?')){
				callApi('POST', config.ORDER_DETAIL_URL, data).then(res => {
					this.getOrderDetail(order.id)
				});
			// }
		} else {
			alert( 'Please choose product or services' )
		}
	}

	handleAddRemain = (event) => {
		event.preventDefault();
		// var time = this.state.date_remain;
		// var data = {time: time, is_send_sms: 0}
		// console.log(config.ORDER_URL + this.state.order.id);
		// callApi('PUT', config.ORDER_URL + this.state.order.id, data).then(res => {
		// 	if(res && res.data.status){
		// 		this.setState({
		// 			date_remain: time
		// 		});
		// 		alert("Set date for customer successfully!")
		// 	}
		// });
	}

	onDelete = (id) => {
		callApi('DELETE', config.ORDER_DETAIL_URL+id, null).then(response => {
			if(response && response.data.status){
				this.getOrderDetail(this.state.order.id);
			}
		});
	}

	render() {
		var { order, customer, order_detail, category_title } = this.state;
		return (

			<CSSTransitionGroup transitionName="worksTransition" transitionAppear={true} transitionAppearTimeout={500} transitionEnter={false} transitionLeave={false}>

				<div className="row">

					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						<div className="row">
							<div className="col-md-12 sortable ui-sortable">
								<div className="grid simple vertical green">
									<div className="grid-title no-border">
										<h4>Infomation Order</h4>
									</div>
									<div className="grid-body no-border">
										<div className="scroll-wrapper scroller " >
											<div className="scroll-element scroll-y scroll-scrollx_visible scroll-scrolly_visible">
												<div className="scroll-element_outer">

													<div className="table-responsive">
														<table className="table table-hover">

															<tbody>
																<tr>
																	<td>ID</td>
																	<td>{order.id}</td>
																</tr>
																<tr>
																	<td>Transaction</td>
																	<td>{order.transaction_id}</td>
																</tr>
																<tr>
																	<td>Customer</td>
																	<td>{order.customer_id}</td>
																</tr>
																<tr>
																	<td>Category</td>
																	<td>{category_title}</td>
																</tr>
																<tr>
																	<td>Note</td>
																	<td>{order.note}</td>
																</tr>

															</tbody>
														</table>
													</div>

												</div>
											</div>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>

					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						<div className="row">
							<div className="col-md-12 sortable ui-sortable">
								<div className="grid simple vertical green">
									<div className="grid-title no-border">
										<h4>Infomation customer</h4>
									</div>
									<div className="grid-body no-border">
										<div className="scroll-wrapper scroller " >
											<div className="scroll-element scroll-y scroll-scrollx_visible scroll-scrolly_visible">
												<div className="scroll-element_outer">
													{typeof customer === 'object' && customer !== null &&

														<div className="table-responsive">
															<table className="table table-hover">

																<tbody>

																	<tr>
																		<td>Name</td>
																		<td> {customer.firstname + " " + customer.lastname }</td>
																	</tr>
																	<tr>
																		<td>Phone</td>
																		<td>{customer.phone}</td>
																	</tr>
																	<tr>
																		<td>Address</td>
																		<td>{customer.address}</td>
																	</tr>
																	<tr>
																		<td>email</td>
																		<td>{customer.email}</td>
																	</tr>
																	<tr>
																		<td>gender</td>
																		<td>{customer.gender === config.GENDER_MALE ? "MALE": "FEMALE"}</td>
																	</tr>

																</tbody>
															</table>
														</div>

													}

												</div>
											</div>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>

					<div className="clearfix"></div>

					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						<div className="row">
							<div className="col-md-12 sortable ui-sortable">
								<div className="grid simple vertical green">
									<div className="grid-title no-border">
										<h4>Add service for customer</h4>
									</div>
									<div className="grid-body no-border">
										<div className="scroll-wrapper scroller scrollbar" >

											<div className="scroll-element scroll-y scroll-scrollx_visible scroll-scrolly_visible">
												<div className="scroll-element_outer">

													<p>
														Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam

													</p>

													<div className="form-group">
														<div className="Product_Service">
															<label>Product/Service</label>
															<select 
																className="form-control" 
																name="product_service_id" 
																value={this.state.product_service_id} 
																onChange={this.onChangeForm}
															>
																<option value="">-- Choose One --</option>
																{this.showCategory()}
															</select>
														</div>
													</div>
													<div className="form-group">
														<label>product_quantity</label>
														<input 
															type="text" 
															className="form-control" 
															onChange={this.onChangeForm} 
															value={this.state.product_quantity} 
															name="product_quantity" 
															placeholder="product_quantity"/>
													</div>

													<p>
														<Button className="btn btn-primary btn-cons" variant="contained" color="secondary" onClick={this.handleAddService}>
															Add Service &nbsp; <i className="material-icons">add</i>
														</Button>
													</p>

												</div>
											</div>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>

					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						<div className="row">
							<div className="col-md-12 sortable ui-sortable">
								<div className="grid simple vertical green">
									<div className="grid-title no-border">
										<h4>Product/Service of <strong> {this.state.customer.firstname} {this.state.customer.lastname} </strong></h4>
									</div>
									<div className="grid-body no-border">
										<div className="scroll-wrapper scroller scrollbar" >

											<div className="scroll-element scroll-y scroll-scrollx_visible scroll-scrolly_visible">
												<div className="scroll-element_outer">

													<OrderData>
														{this.showOrderDetailData(order_detail)} 
													</OrderData>

												</div>
											</div>

										</div>
									</div>
								</div>
							</div>
						</div>
					</div>

					{/* add date remain */}
					<div className="clearfix"></div>

					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						<div className="row">
							<div className="col-md-12 sortable ui-sortable">
								<div className="grid simple vertical green">
									<div className="grid-title no-border">
										<h4>Add Date for remain</h4>
									</div>
									<div className="grid-body no-border">
										<div className="scroll-wrapper scroller scrollbar" >

											<div className="scroll-element scroll-y scroll-scrollx_visible scroll-scrolly_visible">
												<div className="scroll-element_outer">

													<p>
														Lorem ipsum dolor sit amet, consectetur adipiscing elit. Lorem ipsum dolor sit amet 
														yyyy/mm/dd
													</p>

													<div className="form-group">
															<input 
																type="date"
																className="form-control" 
																placeholder="date_remain" 
																name="date_remain" 
																value="10/20/2018"
																// options={{date: true, datePattern: ['Y', 'm', 'd']}}
																onChange={this.onChangeForm}/>
															{/* <Cleave 
																className="form-control" 
																placeholder="date_remain" 
																name="date_remain" 
																value={this.state.date_remain} 
																options={{date: true, datePattern: ['Y', 'm', 'd']}}
																onChange={this.onChangeForm}/> */}
													</div>

													<p>
														<Button className="btn btn-primary btn-cons" variant="contained" color="primary" onClick={this.handleAddRemain}>
															Add Remain &nbsp; <i className="material-icons">add</i>
														</Button>
													</p>

												</div>
											</div>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>

					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						<div className="row">
							<div className="col-md-12 sortable ui-sortable">
								<div className="grid simple vertical green">
									<div className="grid-title no-border">
										<h4>Remain of the day: <strong> {this.state.customer.firstname} {this.state.customer.lastname} </strong></h4>
									</div>
									<div className="grid-body no-border">
										<div className="scroll-wrapper scroller scrollbar" >

											<div className="scroll-element scroll-y scroll-scrollx_visible scroll-scrolly_visible">
												<div className="scroll-element_outer">

													{/* { this.state.date_remain.slice(0,10) } */}

												</div>
											</div>

										</div>
									</div>
								</div>
							</div>
						</div>
					</div>								
				</div>
				</CSSTransitionGroup>
		);
	} // end render

	showOrderDetailData = (order_details) => {
		var result = null;
		if(order_details && typeof order_details !== 'undefined' && order_details.length > 0){
			result = order_details.map((order_detail, index) => {
				return <OrderDataTr key={index} order_detail={order_detail} onDelete={this.onDelete}/>;
			})

		}
		return result;
	}
	showCategory = () => {
		const options = [];
		const {categories} = this.state;
		if (typeof categories === 'object'){
			categories.map(data => options.push(
				<option key={data.id} value={data.id}>{data.title}</option>
			));
		}
		return options;
	}

}

export default OrderActionPage;
