// };

// styles.nav = {
//   padding: 0,
//   margin: 0,
//   position: "absolute",
//   top: 0,
//   height: "40px",
//   width: "100%",
//   display: "flex"
// };

// styles.navItem = {
//   textAlign: "center",
//   flex: 1,
//   listStyleType: "none",
//   padding: "0px"
// };

// styles.hsl = {
//   ...styles.fill,
//   color: "white",
//   paddingTop: "20px",
//   fontSize: "30px"
// };

// styles.rgb = {
//   ...styles.fill,
//   color: "white",
//   paddingTop: "20px",
//   fontSize: "30px"
// };

const mapStateToProps = state => {
	return {
		authentication: state.authentication,
	}
}

// const mapDispatchToProps = (dispatch, props) => {
// 	return {
// 		getUsers : () => {
// 			dispatch(actFetchUsersRequest());
// 		},
// 		onDeleteUser : (id) => {
// 			dispatch(actDeleteUserRequest(id));
// 		}
// 	}
// }

export default connect(mapStateToProps, null)(App);
