import React, { Component } from 'react';
import { Route, Link } from 'react-router-dom';

const menu = [
	{
		name: 'Home',
		to: '/',
		exact: true,
		icon: 'home'
	},
	{
		name: 'Products',
		to: '/products',
		exact: false,
		icon: 'home'
	},
	// {
	// 	name: 'Orders',
	// 	to: '/orders',
	// 	exact: false
	// },
	{
		name: 'Categories',
		to: '/category',
		exact: false,
		icon: 'home'
	},
	{
		name: 'Customers',
		to: '/customers',
		exact: false,
		icon: 'home'
	},

	{
		name: 'Users',
		to: '/users',
		exact: false,
		icon: 'home'
	},
	{
		name: 'SMS',
		to: '/sms',
		exact: false,
		icon: 'home'
	},
	// {
	// 	name: 'Config',
	// 	to: '#',
	// 	exact: false
	// }
];

const MenuLink = ({ label, to, activeOnlyWhenExact, icon}) => {

	return (
		<Route
			path={to}
			exact={activeOnlyWhenExact}
			children={({ match }) => {
				var active = (match && match !== null) ? 'active' : '';
				return (
					<li className={`class-default ${active}`}>
						<Link to={to} className="my-link">
						<i class="material-icons">{icon}</i>
							{label}
						</Link>
					</li>
				)
			}}
		/>
	)
}

class Menu extends Component {

	constructor(props) {
		super(props);
		this.showMenu = this.showMenu.bind(this);
	}
	showMenu(menus) {
		var result = null;
		if (menus.length > 0) {
			result = menus.map((menu, index) => {
				return (<MenuLink label={menu.name} to={menu.to} activeOnlyWhenExact={menu.exact} key={index} icon={menu.icon}/>)
			});
		}
		return result;
	}

	render() {
		return (
				<ul>

					{this.showMenu(menu)}

				</ul>
		);
	}
}

export default Menu;
