import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group'
import ProductsList from '../../components/Product/ProductsList';
// import ProductItem from '../../components/Product/ProductItem';
import * as config from '../../constants/config';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { actFetchProductsRequest, actDeleteProductRequest } from '../../actions/index';

import { Redirect } from 'react-router-dom';

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import matchSorter from 'match-sorter';

class ProductList extends Component {

	constructor(props) {
		super(props);
		this.state = {
			products : [],
			loggedOut: false
		}
	}

	componentWillReceiveProps(nextprops) {
        this.setState({
            loggedOut: nextprops.authentication.loggedOut
        });
	}

	componentWillMount(){
		this.props.getProducts();				
	}

	onDelete = (id) => {
		if (window.confirm('Are you sure you wish to delete this item?')){
			this.props.onDeleteProduct(id)
		}
	}

	currency = (n, currency) => {
		return currency + n.replace(/./g, function(c, i, a) {
			return i > 0 && c !== "." && (a.length - i) % 3 === 0 ? "," + c : c;
		});
	};

	render() {
		if(this.state.loggedOut){
			return <Redirect to={{ pathname: "/"}}/>;
		}

		if (this.props.products !== null) {
			var {products} = this.props.products;
		}

		return (
			<CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>

			<div className="ProductList col-lg-12 col-sm-12 col-xs-12 col-md-12">
			  	<Link to="/products/add" className="btn btn-primary">
				  <i className="fa fa-plus"></i>
			 	</Link>
			 	<br/><br/>
				 <ProductsList>
				 	{products} 
				 </ProductsList>
			 	{/* <ProductsList> */}
			 		{this.showProduct(products)}
			 	{/* </ProductsList>  */}

			</div>
			</CSSTransitionGroup>
		);
	} // end render

	showProduct (products) {
		var result = null;
		if ( products && typeof products !== 'undefined' && products.length > 0) {
			return <ReactTable
						getTdProps={( column ) => ({
							onClick: e => {
								if(column.Header !== 'Action'){
									return (<Link to={`products/1/edit`}>	</Link>);
								}
							}
						})}
						data={products}
						noDataText="Oh Not found!"
						filterable
						defaultFilterMethod={(filter, row) => String(row[filter.id]) === filter.value}
						columns={[
							{
								Header: 'Infomation',
								columns: [
									{
										Header: "#",
										id: "row",
										filterable: false,
										maxWidth: 50,
										Cell: (row) => {
											return <div>{row.index+1}</div>;
										}
									},
									{
										Header: "sku",
										id: "sku",
										accessor: d => d.sku,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["sku"] }),
										filterAll: true,
										maxWidth: 100,
										Cell: (row) => {
											return <div>
												<Link to={`products/${row.original.id}/edit`}>
													  {row.original.sku}
												</Link>
											</div>;
										}
									},
									{
										Header: "Category",
										id: "category_id",
										accessor: d => d.category_id,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["category_id"] }),
										filterAll: true,
										maxWidth: 100

									},
									{
										Header: "title",
										id: "title",
										accessor: d => d.title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["title"] }),
										filterAll: true,
										maxWidth: 600,
										Cell: (row) => {
											return <div className="text-left">
												<Link to={`products/${row.original.id}/edit`}>
													  {row.original.title}
												</Link>
											</div>;
										}
									},
									{
										Header: "price",
										id: "price",
										accessor: d => this.currency(d.price, ''),
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["price"] }),
										maxWidth: 150,
										filterAll: true
									},
									{
										Header: "currency",
										id: "currency",
										accessor: d => d.currency,
										maxWidth: 100,
										Cell: ({ value }) => (value === 'VND' ? "VND" : "USD"),
										filterMethod: (filter, row) => {
											if (filter.value === "all") {
												  return true;
											}
											if (filter.value === String('VND')) {
												return row[filter.id] === 'VND';
											}
											if (filter.value === 'USD') {
												return row[filter.id] === 'USD';
											}

										  },
										  Filter: ({ filter, onChange }) =>
											<select
											  className="sel-role"
											  onChange={event => onChange(event.target.value)}
											  style={{ width: "100%" }}
											  value={filter ? filter.value : "all"}
											>
											  <option value="all">Show All</option>
											  <option value="VND">VND</option>
											  <option value="USD">USD</option>
											</select>
									},
									{
										Header: "unit",
										id: "unit",
										accessor: d => d.unit,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["unit"] }),
										maxWidth: 100,
										filterAll: true
									},
									{
										Header: "quantity",
										id: "quantity",
										accessor: d => d.quantity,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["quantity"] }),
										filterAll: true,
										maxWidth: 100,
									},
									{
										Header: "is_publish",
										id: "is_publish",
										accessor: d => d.is_publish,
										maxWidth: 100,
										Cell: ({ value }) => (value === config.IS_PUBLISH_YES ? "Yes" : "No"),
										filterMethod: (filter, row) => {
											if (filter.value === "all") {
												  return true;
											}
											if (filter.value === String(config.IS_PUBLISH_YES)) {
												return row[filter.id] === config.IS_PUBLISH_YES;
											}
											if (filter.value === String(config.IS_PUBLISH_NO)) {
												return row[filter.id] === config.IS_PUBLISH_NO;
											}

										  },
										  Filter: ({ filter, onChange }) =>
											<select
											  className="sel-role"
											  onChange={event => onChange(event.target.value)}
											  style={{ width: "100%" }}
											  value={filter ? filter.value : "all"}
											>
											  <option value="all">Show All</option>
											  <option value="1">Yes</option>
											  <option value="0">No</option>
											</select>
									},
									{
										Header: "Action",
										filterable: false,
										maxWidth: 100,
										Cell: row => (

												<button type="button" className="btn btn-danger" onClick={ () => this.onDelete(row.original.id)}><i className="fa fa-trash"></i></button>

										)
									}
								]
							}
					]}
					defaultSorted={[{
						id: "row",
						desc: true
					}]}
					defaultPageSize={20}  
					style={{
						height: "800px" // This will force the table body to overflow and scroll, since there is not enough room
					}}
					className="-striped -highlight"
				/>
		}
		// if (products) {
			// result = products.map((product, index) => {
			// 	console.log(product);
			// 	return (<ProductItem key={index} product={product} index={index} onDelete={this.onDelete}/>);
			// });
		// }
		return result;
	}
}

const mapStateToProps = (state) => {
	return {
		products: state.products,
		authentication: state.authentication
	};
}
const mapDispatchToProps = (dispatch, props) => {
	return {
		getProducts : () => {
			dispatch(actFetchProductsRequest());
		},
		onDeleteProduct : (id) => {
			dispatch(actDeleteProductRequest(id));
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(ProductList);
