import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import {ORDER_URL} from './../../constants/config';
import callApi from './../../utils/apiCaller';

import Swal from 'sweetalert2'

class AddDoctor extends Component {

    constructor(props){
		super(props);
		this.state = {
            user_id: null,
            doctor: ''
		};
	}

    onChangeForm = (event) => {
		var target = event.target;
		var name = target.name;
		var value = target.type === 'checkbox'? target.checked:target.value;
		this.setState({
			[name]: value
		});
	}

    handleAddDoctor () {
        var {user_id} = this.state;
        // this.props.handleAddDoctor(user_id);
        console.log(user_id);
        if( user_id !== null) {
            var data = {user_id: user_id};
            callApi('POST', ORDER_URL + 'user/' + this.props.order_id, data).then(res => {
                if ( res.data.status ) {
                    this.setState({
                        doctor: res.data.data.username
                    });
                }
            });
        } else {
            Swal('Oops...', 'Something went wrong!', 'error')
        }
	}

    render() {
        return (
            <div>
                <div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
                    <div className="row">
                        <div className="col-md-12 sortable ui-sortable">
                            <div className="grid simple vertical green">
                                <div className="grid-title no-border">
                                    <h4>Add doctor for customer</h4>
                                </div>
                                <div className="grid-body no-border">

                                        <div className="form-group">
                                            <select 
                                                className="form-control" 
                                                name="user_id" 
                                                onChange={this.onChangeForm}
                                            >
                                                <option value={this.state.user_id}>-- Choose One --</option>
                                                <option value="1">-- Doctor 1 --</option>
                                                <option value="2">-- Doctor 2 --</option>
                                                <option value="3">-- Doctor 3 --</option>
                                            </select>


                                        </div>
                                        <p>
                                            <Button className="btn btn-primary btn-cons" variant="contained" color="secondary" onClick={ () => this.handleAddDoctor()}>
                                                Add Doctor &nbsp; <i className="material-icons">person_add</i>
                                            </Button>
                                        </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						<div className="row">
							<div className="col-md-12 sortable ui-sortable">
								<div className="grid simple vertical green">
									<div className="grid-title no-border">
										<h4>Doctor</h4>
									</div>
									<div className="grid-body no-border">
										<div className="scroll-wrapper scroller scrollbar" >

											<div className="scroll-element scroll-y scroll-scrollx_visible scroll-scrolly_visible">
												<div className="scroll-element_outer">

													{this.state.doctor}

												</div>
											</div>

										</div>
									</div>
								</div>
							</div>
						</div>
					</div>
            </div>

        );
    }
}

export default AddDoctor;
