import * as Types from './../constants/ActionType';

// const initialState = { loggedIn: sessionStorage.getItem('authentication') ? true : false, loggedOut: false };
const initialState = { loggedIn: null, loggedOut: false };

const authentication = (state = initialState, action) => {
    switch(action.type){
        case Types.LOGIN:
            if( action.user ){
                // sessionStorage.setItem('authentication', JSON.stringify(action.user));
                state = { loggedIn: action.user.status, loggedOut: false, user: JSON.parse(sessionStorage.getItem('authentication')) };
                // state = { loggedIn: action.user.status, loggedOut: false };
            }
            return state;
        case Types.LOGOUT:
            sessionStorage.removeItem('authentication');
            state = { loggedIn: false, loggedOut: true, user: null };
            return state;
        default: 
            return state;
    }
}

export default authentication;
