import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group';
import { Link } from 'react-router-dom';
import * as config from './../../constants/config';
import callApi from './../../utils/apiCaller';
import Cleave from 'cleave.js/react';

const categoryData = [
	{ value: 'chocolate', label: 'Chocolate' },
	{ value: 'strawberry', label: 'Strawberry' },
	{ value: 'vanilla', label: 'Vanilla' }
];


class ProductActionPage extends Component {

	constructor(props){
		super(props);
		this.state = {
			id: '',
			sku: '',
			title: '',
			price: '',
			currency: '',
			unit: '',
			quantity: 0,
			is_publish: 1,
			note: ''
		};
	}

	componentDidMount(){
		var {match} = this.props;
		if(match) {
			var id = match.params.id;
			callApi('GET', config.PRODUCT_URL+id, null).then(res => {
				var data = res.data.data;
				this.setState({
					id: data.id ? data.id : '',
					sku: data.sku ? data.sku : '',
					title: data.title ? data.title : '',
					price: data.price ? data.price : '',
					quantity: data.quantity ? data.quantity : '',
					unit: data.unit ? data.unit : '',
					currency: data.currency ? data.currency : '',
					note: data.note ? data.note : '',
					is_publish: data.is_publish? data.is_publish : ''
				});
			});
		}
	}

	onChangeForm = (event) => {
		var target = event.target;
		var name = target.name;
		var value = target.type === 'checkbox'? target.checked:target.value;
		this.setState({
			[name]: value
		});
	}

	priceVal = (price) => {
		return parseFloat(price.replace(/[^0-9-.]/g, ''));
	};

	onSave = (event) => {
		event.preventDefault();
		var {history} = this.props;
		var {id, sku, title, price, quantity, unit, is_publish, currency, note} = this.state;
		var data = {sku: sku, title: title, price: this.priceVal(price), quantity: quantity,  currency: currency,  note: note, unit: unit, is_publish: is_publish ? true : false };
		if(id) { //update
			callApi('PUT', config.PRODUCT_URL + id, data).then( res => {
				history.push("/products");
			});
		} else { //create
			callApi('POST', config.PRODUCT_URL, data ).then( res => {
				//C1 // history.push("/products-list");
				// console.log(res);
				// history.push("/products");
				//C2: redirect 
				history.goBack();
			});
		}
	}

	render() {
		return (
			<CSSTransitionGroup transitionName="worksTransition" transitionAppear={true} transitionAppearTimeout={500} transitionEnter={false} transitionLeave={false}>

				<div>
					<div className="col-lg-3 col-sm-3 col-xs-3 col-md-3">
					</div>
					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">

						<form onSubmit={this.onSave}>
							<legend>Form title</legend>

							<div className="form-group">
								<label>sku</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.sku} 
									onChange={this.onChangeForm} 
									name="sku" 
									placeholder="sku"/>
							</div>
							<div className="form-group">
								<label>title</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.title} 
									onChange={this.onChangeForm} 
									name="title" 
									placeholder="title"/>
							</div>
							<div className="form-group">
								<label>Price</label>
								<Cleave className="input-numeral form-control" 
									placeholder="Enter numeral" 
									name="price"
									value={this.state.price} 
									options={{numeral: true, numeralThousandsGroupStyle: 'thousand'}}
                        			onChange={this.onChangeForm}
								/>
							</div>
							<div className="form-group">
								<label>quantity</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.quantity} 
									onChange={this.onChangeForm} 
									name="quantity" 
									placeholder="quantity"/>
							</div>
							<div className="form-group">
								<label>unit</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.unit} 
									onChange={this.onChangeForm} 
									name="unit" 
									placeholder="unit"/>
							</div>

							{/* <div className="form-group">
								<div className="currency">
									<label>currency</label>
									<select
										className="form-control"
										name="currency"
										value={this.state.currency} 
										onChange={this.onChangeForm}
									>
										<option value={config.CURRENCY_VND}>{config.CURRENCY_VND}</option>
										<option value={config.CURRENCY_USD}>{config.CURRENCY_USD}</option>
									</select>
								</div>
							</div> */}

							<div className="form-group">
								<div className="currency">
									<label>currency</label>
									<select
										className="form-control"
										name="currency"
										value={this.state.currency} 
										onChange={this.onChangeForm}
									>
										<option value={config.CURRENCY_VND}>{config.CURRENCY_VND}</option>
										<option value={config.CURRENCY_USD}>{config.CURRENCY_USD}</option>
									</select>
								</div>
							</div>

							<div className="form-group">
								<label>Publish</label>
								<div className="checkbox">
									<label>
										<input 
											type="checkbox" 
											value={this.state.is_publish} 
											checked={this.state.is_publish} 
											onChange={this.onChangeForm} 
											name="is_publish"/>
										Publish
									</label>
								</div>
							</div>
							<div className="form-group">
								<label>note</label>

								<textarea name="note" id="input" onChange={this.onChangeForm} value={this.state.note}  className="form-control" rows="3" required="required">
									{this.state.note} 
								</textarea>


							</div>
							{this.showCategory()}

							<button type="submit" className="btn btn-primary margin-right-10">Save</button>
							<Link to="/products" className="btn btn-success">
									Back
							</Link>
						</form>
						<br/><br/>
					</div>
				</div>
				</CSSTransitionGroup>
		);
	} // end render

	showCategory(){
		var DropDown = React.createClass(
			{
				render: function () {
					var items = this.props.data;
					return (
					<select value={this.props.Selected}>
						{
							items.map(function (item) {
								return <option value={item.Name }>{item.Name}</option>;
							})
						}
					</select>);
				}
			});
			return DropDown;
		// var result = [];
		// categoryData.map((category, index) => {
		// 	// console.log(category.value);
		// 	// return category.value;
		// 	// return (<option value={category.value}>{category.label}</option>);
		// });
		// return 'ok';
		// console.log(categoryData);
	}


}

export default ProductActionPage;
