import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group'
import * as config from '../../constants/config';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { actFetchOrdersRequest, actDeleteOrderRequest } from '../../actions/index';

import { Redirect } from 'react-router-dom';

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import matchSorter from 'match-sorter';

class OrderList extends Component {

	constructor(props) {
		super(props);
		this.state = {
			orders : [],
			loggedOut: false
		}

		this.onDelete = this.onDelete.bind(this);
	}

	componentWillReceiveProps(nextprops) {
        this.setState({
            loggedOut: nextprops.authentication.loggedOut
        });
	}

	componentWillMount(){
		this.props.getOrders();
	}

	onDelete (id) {
		if (window.confirm('Are you sure you wish to delete this item?')){
			this.props.onDeleteOrder(id)
		}
	}

	render() {
		if(this.state.loggedOut){
			return <Redirect to={{ pathname: "/"}}/>;
		}

		if (this.props.orders !== null) {
			var {orders} = this.props.orders;
		}

		return (
			<CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
				<div className="row">
					<div className="col-md-12 sortable ui-sortable">
						<div className="grid simple vertical ">
							<div className="grid-title no-border">
							<div className="page-title"> 
								<i className="material-icons">arrow_back</i>
								<h3> <span className="semi-bold">Orders</span></h3>
							</div>
									{/* <Link to="/orders/add" className="btn btn-primary">
									<i className="fa fa-plus"></i>
									</Link>
									<br/><br/> */}
									{this.showOrder(orders)}
								</div>
							</div>
						</div>
					</div>

			</CSSTransitionGroup>
		);
	} // end render

	showOrder (orders) {
		var result = null;
		if ( orders && typeof orders !== 'undefined' && orders.length > 0) {
			return <ReactTable
						getTdProps={( column ) => ({
							onClick: e => {
								if(column.Header !== 'Action'){
									return (<Link to={`orders/1/edit`}>	</Link>);
								}
							}
						  })}
						data={orders}
						noDataText="Oh Not found!"
						filterable
						defaultFilterMethod={(filter, row) =>
							String(row[filter.id]) === filter.value}
						columns={[
							{
								Header: 'Infomation',
								columns: [
									{
										Header: "#",
										id: "row",
										filterable: false,
										maxWidth: 50,
										Cell: (row) => {
											return <div>{row.index+1}</div>;
										}
									},
									{
										Header: "transaction_id",
										id: "transaction_id",
										accessor: d => d.transaction_id,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["transaction_id"] }),
										filterAll: true,
										maxWidth: 200,
										Cell: (row) => {
											return <div>
												<Link to={`orders/${row.original.id}/${row.original.customer_id}/edit`}>
													  {row.original.transaction_id}
												</Link>
											</div>;
										}
									},
									{
										Header: "title",
										id: "title",
										accessor: d => d.title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["title"] }),
										filterAll: true,
										maxWidth: 600,
										Cell: (row) => {
											return <div>
												<Link to={`orders/${row.original.id}/${row.original.customer_id}/edit`}>
													  {row.original.title}
												</Link>
											</div>;
										}
									},
									{
										Header: "customer_title",
										id: "customer_title",
										accessor: d => d.customer_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["customer_title"] }),
										maxWidth: 250,
										filterAll: true
									},
									{
										Header: "user_title",
										id: "user_title",
										accessor: d => d.user_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["user_title"] }),
										maxWidth: 100,
										filterAll: true
									},
									{
										Header: "Category",
										id: "product_category_id",
										accessor: d => d.product_category_id,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["product_category_id"] }),
										filterAll: true,
										maxWidth: 100,
									},
									{
										Header: "status",
										id: "status",
										accessor: d => d.status,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["product_category_id"] }),
										filterAll: true,
										maxWidth: 100,
									},
									{
										Header: "Action",
										filterable: false,
										maxWidth: 100,
										Cell: row => (

												<button type="button" className="btn btn-danger" onClick={ () => this.onDelete(row.original.id)}><i className="fa fa-trash"></i></button>

										)
									}
								]
							}
					]}
					defaultSorted={[
						{
						  id: "row",
						  desc: true
						}
					  ]}
					defaultPageSize={20}  
					style={{
						height: "800px" // This will force the table body to overflow and scroll, since there is not enough room
					  }}
					className="-striped -highlight"
				/>
		}

		return result;
	}
}

const mapStateToProps = (state) => {
	return {
		orders: state.orders,
		authentication: state.authentication
	};
}
const mapDispatchToProps = (dispatch, props) => {
	return {
		getOrders : () => {
			dispatch(actFetchOrdersRequest());
		},
		onDeleteOrder : (id) => {
			dispatch(actDeleteOrderRequest(id));
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(OrderList);
