import React, { Component } from 'react';
import './App.css';
import { BrowserRouter , Route, Switch } from 'react-router-dom';
import Menu from './components/Menu/Menu';
import Login from './pages/Login/Login';
import routes from './routes';
import { ISLOGIN } from './constants/config';
import { TransitionGroup, CSSTransition } from "react-transition-group";
import { connect } from 'react-redux';

class App extends Component {

	constructor(props){
		super(props)
		this.state = {
			isLogin : ISLOGIN
		}
	}

	componentWillReceiveProps(nextprops){

		this.setState({
			isLogin: nextprops.authentication.loggedIn
		});	

	}

	render() {
		if( this.state.isLogin ) {
			return (
				<BrowserRouter>
					<Route render={({ location }) => (
						<div style={styles.content}>
							<Menu />
							<TransitionGroup>
							<CSSTransition key={location.key} classNames="fade" timeout={300}>

							<Switch location={location}>

								{this.showContentMenu(routes,location)}

							</Switch>
							</CSSTransition>

							</TransitionGroup>
						</div>
					)}/>
				</BrowserRouter>
			);
		} else {
			return (
				<BrowserRouter>
					<Route render={({ location }) => (
						<div className="App" >

							<Login />

						</div>
					)}/>
				</BrowserRouter>
			);
		}

	}

	showContentMenu = (routes,location) => {
		var result = null;
		if(routes.length > 0){
			result = routes.map((route, index) => {
				return (
					<Route key={index} path={route.path} exact={route.exact} component={route.main} />
				);
			})
		}
		return result;
	}

}
const styles = {};

styles.fill = {
  position: "absolute",
  left: 0,
  right: 0,
  top: 0,
  bottom: 0
};

styles.content = {
  ...styles.fill,
  top: "0px",
//   textAlign: "center"
};

styles.nav = {
  padding: 0,
  margin: 0,
  position: "absolute",
  top: 0,
  height: "40px",
  width: "100%",
  display: "flex"
};

styles.navItem = {
  textAlign: "center",
  flex: 1,
  listStyleType: "none",
  padding: "0px"
};

styles.hsl = {
  ...styles.fill,
  color: "white",
  paddingTop: "20px",
  fontSize: "30px"
};

styles.rgb = {
  ...styles.fill,
  color: "white",
  paddingTop: "20px",
  fontSize: "30px"
};

const mapStateToProps = state => {
	return {
		authentication: state.authentication,
	}
}

// const mapDispatchToProps = (dispatch, props) => {
// 	return {
// 		getUsers : () => {
// 			dispatch(actFetchUsersRequest());
// 		},
// 		onDeleteUser : (id) => {
// 			dispatch(actDeleteUserRequest(id));
// 		}
// 	}
// }

export default connect(mapStateToProps, null)(App);
