import React, { Component } from 'react';
import { Route, Link } from 'react-router-dom';
import LoginButton from "./LoginButton";
import { connect } from 'react-redux';

const menu = [
	{
		name: 'Home',
		to: '/',
		exact: true
	},
	{
		name: 'Users',
		to: '/users',
		exact: false
	},
	{
		name: 'About',
		to: '/about',
		exact: false
	},
	{
		name: 'Products',
		to: '/products-list',
		exact: false
	}
];

const MenuLink = ({ label, to, activeOnlyWhenExact }) => {

	return (
		<Route
			path={to}
			exact={activeOnlyWhenExact}
			children={({ match }) => {
				var active = (match && match !== null) ? 'active' : '';
				return (
					<li className={`class-default ${active}`}>
						<Link to={to} className="my-link">
							{label}
						</Link>
					</li>
				)
			}}
		/>
	)
}

class Menu extends Component {

	constructor(props) {
		super(props);
		this.state = {
			loggedOut: false
		}
		this.showMenu = this.showMenu.bind(this);
	}
	showMenu(menus) {
		var result = null;
		if (menus.length > 0) {
			result = menus.map((menu, index) => {
				return (<MenuLink label={menu.name} to={menu.to} activeOnlyWhenExact={menu.exact} key={index} />)
			});
		}
		return result;
	}

	componentWillReceiveProps(nextprops) {
        this.setState({
            loggedOut: nextprops.authentication.loggedOut 
		});
		console.log(this.state.loggedOut);
    }

	render() {
		return (
			<nav className="navbar navbar-inverse">
				<ul className="nav navbar-nav">
					{this.showMenu(menu)}

					<LoginButton />
				</ul>
			</nav>
		);
	}
}


const mapStateToProps = state => {
	return {
		loggedOut: state.authentication
	}
}




export default connect(mapStateToProps, null)(Menu);
