import React, { Component } from 'react';

class OrderDataTr extends Component {
    onDelete (id) {
        if (window.confirm('Are you sure you wish to add this item?')){

        this.props.onDelete(id);
        }
	}

    render() {
        var { order_detail } = this.props;
        return (

            <tr>
                <td>{order_detail.id}</td>
                <td>{order_detail.order_id}</td>	
                <td>{order_detail.product_title}</td>
                <td>{order_detail.product_price}</td>
                <td>{order_detail.quantity}</td>
                <td><button type="button" className="btn btn-danger" onClick={ () => this.onDelete(order_detail.id)}>Delete</button></td>
            </tr>


        );
    }
}

export default OrderDataTr;
