import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group';

import { Link } from 'react-router-dom';
import * as config from './../../constants/config';
import callApi from './../../utils/apiCaller';
class ProductActionPage extends Component {

	constructor(props){
		super(props);
		this.state = {
			id: '',
			firstname: '',
			lastname: '',
			txtAvatar: '',
			txtchbox: false

		};
		this.onSave = this.onSave.bind(this);
		this.onChangeFrom = this.onChangeFrom.bind(this);
	}

	componentDidMount(){
		var {match} = this.props;
		if(match) {
			var id = match.params.id;
			callApi('GET', config.APP_URL+'/'+id, null).then(res => {
				var data = res.data.data;
				this.setState({
					id: data.id,
					firstname: data.first_name,
					lastname: data.last_name,
					txtAvatar: data.avatar
				});
			});
		}
	}

	onChangeFrom (event) {
		var target = event.target;
		var name = target.name;
		var value = target.type === 'checkbox'? target.checked:target.value;
		this.setState({
			[name]: value
		});

	}

	onSave (event) {
		event.preventDefault();

		var {history} = this.props;
		var {id, firstname, lastname,txtAvatar} = this.state;
		if(id) { //update
			var data = {firstname: firstname, lastname: lastname, txtAvatar: txtAvatar};
			callApi('PUT', config.APP_URL+'/'+ id, data).then( res => {
				history.push("/products-list");
			});
		} else { //create
			callApi('POST', config.APP_URL, {
				firstname: firstname,
				lastname: lastname,
				txtAvatar: txtAvatar
			}).then( res => {
				//C1 // history.push("/products-list");
				history.push("/");
				//C2: redirect 
				// history.goBack();
			});
		}
	}

	render() {
		return (
			<CSSTransitionGroup transitionName="worksTransition" transitionAppear={true} transitionAppearTimeout={500} transitionEnter={false} transitionLeave={false}>

				<div>

					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">

						<form onSubmit={this.onSave}>
							<legend>Form title</legend>

							<div className="form-group">
								<label>FirstName</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.firstname} 
									onChange={this.onChangeFrom} 
									name="firstname" 
									placeholder="FirstName"/>
							</div>
							<div className="form-group">
								<label>LastName</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.lastname} 
									onChange={this.onChangeFrom} 
									name="lastname" 
									placeholder="LastName"/>
							</div>
							<div className="form-group">
								<label>Avatar</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.txtAvatar} 
									onChange={this.onChangeFrom} 
									name="txtAvatar" 
									placeholder="Avatar"/>
							</div>
							<div className="form-group">
								<label>Status</label>

								<div className="checkbox">
									<label>
										<input 
											type="checkbox" 
											value={this.state.txtchbox} 
											// checked 
											onChange={this.onChangeFrom} 
											name="txtchbox"/>
										Con hang
									</label>
								</div>

							</div>

							<button type="submit" className="btn btn-primary margin-right-10">Save</button>
							<Link to="/products-list" className="btn btn-success">
									Back
							</Link>
						</form>
					</div>
				</div>
			</CSSTransitionGroup>
		);
	} // end render


}

export default ProductActionPage;
