import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group';
// import { Link } from 'react-router-dom';
import callApi from './../../utils/apiCaller';
import * as config from './../../constants/config';
import Button from '@material-ui/core/Button';
import Swal from 'sweetalert2';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import ListItemText from '@material-ui/core/ListItemText';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';
import {findIndex} from 'lodash';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
	PaperProps: {
		style: {
			maxHeight: ITEM_HEIGHT * 8.5 + ITEM_PADDING_TOP,
			width: 250,
		},
	},
};

const names = [
	{ id: 1, name: 'Oliver Hansen'},
	{ id: 2, name: 'Van Henry'},
	{ id: 3, name: 'April Tucker'},
	{ id: 4, name: 'Ralph Hubbard'},
	{ id: 5, name: 'Omar Alexander'},
	{ id: 6, name: 'Miriam Wagner'},
	{ id: 7, name: 'Bradley Wilkerson'},
];
class Permissions extends Component {

    constructor (props) {
        super(props)
        this.state = {
			name: ['Oliver Hansen'],
            isFormValidationErrors : true,
			submitted: false,
        }
	}
	handleChange = event => {
		this.setState({ name: event.target.value });
	};

    isValidationError = (flag) => {
		this.setState({isFormValidationErrors:flag});
   	}

    handleFormSubmit = (event) => {
		event.preventDefault();
		var array = [];

		this.state.name.map( (n) => {
			var p = (findIndex(names, { 'name': n }));
			if(p !== -1) {
				array.push(names[p].id);
			}
		});

		var data = { 
			array
		};
		let { isFormValidationErrors } = this.state;
        if ( !isFormValidationErrors ){
			callApi('POST', config.API_URL + "change-password", data).then( res => {
                console.log(res);
                if(res){
                    if( res.data.status ){
                        Swal(' Good job!', res.data.message, 'success')
                    } else {
                        Swal('Oops...', res.data.message, 'error')
                    }
                }
            });
        } else {
            Swal('Oops...', 'Something went wrong!', 'error')
        }
    }

    render() {

        return (
            <React.Fragment>
                <CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
                    <div className="grid simple">
						<div className="grid-body no-border">

							<form noValidate onSubmit={this.handleFormSubmit}>
								<div className="col-lg-6 col-md-6">

									<div className="form-group">
										<label>Users</label>
										<Select
											multiple
											value={this.state.name}
											onChange={this.handleChange}
											input={<Input id="select-multiple-checkbox" />}
											renderValue={selected => selected.join(', ')}
											MenuProps={MenuProps}
										>
											{names.map(name => (
												<MenuItem key={name.id} value={name.name}>
													<Checkbox checked={this.state.name.indexOf(name.name) > -1} />
													<ListItemText primary={name.name} />
												</MenuItem>
											))}
										</Select>
									</div>

								</div>
								<div className="col-lg-12 col-md-12">
									<Button type="submit" variant="contained" color="primary">Save</Button>
								</div>
							</form>

						</div>
					</div>
                </CSSTransitionGroup>
            </React.Fragment>
        );
    }
}

export default Permissions;
