import React, { Component } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Menu from './components/Menu/Menu';
import Login from './pages/Login/Login';
import routes from './routes';
import { ISLOGIN } from './constants/config';
// import { TransitionGroup, CSSTransition } from "react-transition-group";
import { connect } from 'react-redux';

class App extends Component {

	constructor(props){
		super(props)
		this.state = {
			isLogin : ISLOGIN
		}
	}

	// shouldComponentUpdate(nextprops){
	// 	console.log(this.state.isLogin);
	// 	this.setState({
	// 		isLogin: true,
	// 	});	
	// 	console.log(this.state.isLogin);
	// 	return true;
	// }

	componentWillUpdate(nextprops){
		console.log(nextprops);
		if(nextprops.authentication && !nextprops.authentication.loggedIn ){
			this.setState({
				isLogin: true
			});	
		}

		if(nextprops.authentication && !nextprops.authentication.loggedOut ){
			this.setState({
				isLogin: false
			});	
		}
	}

	render() {
		if( this.state.isLogin ) {
			return (
				<Router>
					<Route render={({ location }) => (
						<div className="App" >
							<Menu />

							<Switch location={location}>

								{this.showContentMenu(routes,location)}

							</Switch>

						</div>
					)}/>
				</Router>
			);
		} else {
			return (
				<Router>
					<Route render={({ location }) => (
						<div className="App" >

							<Login />

						</div>
					)}/>
				</Router>
			);
		}

	}

	showContentMenu = (routes,location) => {
		var result = null;
		if(routes.length > 0){
			result = routes.map((route, index) => {
				return (
					<Route 
						key={index} 
						path={route.path} 
						exact={route.exact} 
						component={route.main} 
					/>
				);
			})
		}
		return result;
	}

}
