import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import matchSorter from 'match-sorter';
class OrderList extends Component {

    constructor(props) {
		super(props);
		this.state = {
			orders : [],
			loggedOut: false,
			open: false,
			order_id: ''
		}
		// connectIO(message => {
		// 	this.props.getOrders();
		// 	Swal('Good job!','You clicked the button!','success')
		// });
		this.onDelete = this.onDelete.bind(this);
	}

    onOpenModal = (id) => {
		this.setState({ order_id: id,open: true });
	};

	onCloseModal = () => {
		this.setState({ open: false });
    };

    render() {
        var orders = this.props.children;

        return (
            <div>
                {orders && typeof orders !== 'undefined' && orders.length > 0  ? (
                <ReactTable
						// getTdProps={( column ) => ({
						// 	onClick: e => {
						// 		if(column.Header !== 'Action'){
						// 			return (<Link to={`orders/1/edit`}>	</Link>);
						// 		}
						// 	}
						//   })}
						data={orders}
						noDataText="Oh Not found!"
						filterable
						defaultFilterMethod={(filter, row) =>
							String(row[filter.id]) === filter.value}
						columns={[
							{
								Header: 'Infomation',
								columns: [
									{
										Header: "#",
										id: "row",
										filterable: false,
										maxWidth: 50,
										Cell: (row) => {
											return <div>{row.index+1}</div>;
										}
									},
									{
										Header: "transaction_id",
										id: "transaction_id",
										accessor: d => d.transaction_id,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["transaction_id"] }),
										filterAll: true,
										maxWidth: 200,
										Cell: (row) => {
											return <div>
												<Link to={`orders/edit/${row.original.id}/${row.original.customer_id}`}>
													  {row.original.transaction_id}
												</Link>
											</div>;
										}
									},						
									{
										Header: "customer_title",
										id: "customer_title",
										accessor: d => d.customer_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["customer_title"] }),
										maxWidth: 250,
										filterAll: true
									},
									{
										Header: "user_title",
										id: "user_title",
										accessor: d => d.user_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["user_title"] }),
										maxWidth: 300,
										filterAll: true
									},
									{
										Header: "Category",
										id: "category_title",
										accessor: d => d.category_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["category_title"] }),
										filterAll: true,
										maxWidth: 300,
									},
									{
										Header: "status",
										id: "status",
										accessor: d => d.status,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["status"] }),
										filterAll: true,
										maxWidth: 100,
									},
									{
										Header: "Action",
										filterable: false,
										maxWidth: 100,
										Cell: row => (
											<div>
												<Button type="submit" className="btn btn-primary btn-cons-small" variant="fab" color="primary" size="small" onClick={ () => this.onOpenModal(row.original.id) }>
													<i className="material-icons">home</i>
												</Button>

												<Button type="submit" className="btn btn-primary btn-cons-small" variant="fab" color="secondary" size="small"  onClick={ () => this.onDelete(row.original.id)}>
													<i className="material-icons">delete</i>
												</Button>
											</div>


										)
									}
								]
							}
					]}
					defaultSorted={[
						{
						  id: "row",
						  desc: true
						}
					  ]}
					defaultPageSize={10}  
					className="-striped -highlight"
				/>
                ) : ( // else 
					<div></div>
				)}
            </div>

        );
    }
}

export default OrderList;
