import React, { Component } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './components/Elements/Navbar';
import Sidebar from './components/Elements/Sidebar';
import Login from './pages/Login/Login';
import routes from './routes';
import { ISLOGIN } from './constants/config';
import { connect } from 'react-redux';
import { CSSTransitionGroup } from 'react-transition-group'
import * as config from './constants/config';
import Forbidden from './pages/NotFound/403_Forbidden'; // 403
import {findIndex, split} from 'lodash';
import callApi from './utils/apiCaller';

class App extends Component {

	constructor(props){
		super(props)
		this.state = {
			isLogin : ISLOGIN,
			pages: ''
		}
	}

	componentDidMount(){
		const ele = document.getElementById('ipl-progress-indicator')
		if(ele){
		  // fade out
		  ele.classList.add('available')
		  setTimeout(() => {
			// remove from DOM
			ele.outerHTML = ''
		  },100)
		}

		callApi('GET', config.USERS_URL + 'list-page/86', null).then( res => {
			this.setState({
				pages: res.data.data
			});
		});

	}

	componentWillReceiveProps(nextprops){
		this.setState({
			isLogin: nextprops.authentication.loggedIn
		});	
	}

	render() {
		if( this.state.isLogin ) {
			return (
				<Router>
					<Route render={({ location }) => (
						<CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
							<div className="App" >
								<Navbar/>
								<div className="page-container row-fluid">
									<Sidebar/>
									<div className="page-content container">
										<div className="content sm-gutter">
										<div className="page-title"></div>
										</div>
										{/* <div className="grid simple ">
											<div className="grid-body no-border"> */}
												<Switch location={location}>
													{this.showContentMenu(routes,location)}
												</Switch>
											{/* </div>
										</div> */}
									</div>
								</div>
							</div>
						</CSSTransitionGroup>
					)}/>
				</Router>
			);
		} else {
			return (
				<CSSTransitionGroup transitionName={config.LOGINTRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
					<Login />
				</CSSTransitionGroup>
			);
		}

	}

	isAuthenticated = (location) => {
		if(this.state.pages !== ''){
			// var pages = [
			// 	{ 'page': '' },
			// 	{ 'page': 'sms' },
			// 	{ 'page': 'products' },
			// 	{ 'page': 'orders' },
			// 	{ 'page': 'customers' },
			// 	{ 'page': 'customers/add' },
			// 	{ 'page': 'customers/205' },
			// ];
			var {pages} = this.state;
			var path = null;
			var arr = split(location.pathname,'/', 3);
			if(arr[2]) {
				path = arr[1] + "/" + arr[2];
			} else{
				path = arr[1];
			}
			if( findIndex(pages, { 'page': path }) === -1 ) {
				return true;
			}
			return false;
		}
		return false;
	}

	showContentMenu = (routes,location) => {
		var result = null;
		if(config.ROLE !== 14){
			if( this.isAuthenticated(location) ){
				return <Route exact={false} path='/' component={Forbidden}/> // ===> redirect to 403
			}
		}

		if(routes.length > 0){
			result = routes.map((route, index) => {
				return (
					<Route 
						key={index} 
						path={route.path} 
						exact={route.exact} 
						component={route.main} 
					/>
				);
			})
		}
		return result;
	}

}

const mapStateToProps = state => {
	return {
		authentication: state.authentication,
	}
}

// const mapDispatchToProps = (dispatch, props) => {
// 	return {
// 		getUsers : () => {
// 			dispatch(actFetchUsersRequest());
// 		},
// 		onDeleteUser : (id) => {
// 			dispatch(actDeleteUserRequest(id));
// 		}
// 	}
// }

export default connect(mapStateToProps, null)(App);
