import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group'
import callApi from './../../utils/apiCaller';
import { connect } from "react-redux";
import { Redirect } from 'react-router-dom';
import * as config from '../../constants/config';
import Validator from 'react-forms-validator';
import { Link } from 'react-router-dom';
class CustomerActionPage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			image: '',
			loggedOut: false
		}
		this.state = {
			id: '',
			username: '',
			firstname: '',
			lastname: '',
			email: '',
			address: '',
			phone: '',
			gender: config.GENDER_FEMALE,
			isFormValidationErrors : true,
			submitted: false,
			isValidation: '',
			loggedOut: false
		};
		// this.onFormSubmit = this.onFormSubmit.bind(this)
		// this.onChange = this.onChange.bind(this)
	}

	componentWillReceiveProps(nextprops) {
        this.setState({
            loggedOut: nextprops.authentication.loggedOut
        });
	}

	componentDidMount(){
		var {match} = this.props;
		if(match) {
			var id = match.params.id;
			callApi('GET', config.CUSTOMER_URL + id, null).then(res => {
				var data = res.data.data;
				this.setState({
					id: data.id,
					username: data.username,
					firstname: data.firstname,
					lastname: data.lastname,
					email: data.username,
					address: data.address,
					phone: data.phone,
					gender: data.gender
				});
			});
		}
	}

	isValidationError(flag){
		this.setState({isFormValidationErrors:flag});
   	}

	onChangeForm (event) {
		var target = event.target;
		var name = target.name;
		var value = target.type === 'checkbox'? target.checked:target.value;
		this.setState({
			[name]: value
		});

		if(target.type === 'file'){
			let files = event.target.files || event.dataTransfer.files;
			if (!files.length)
				return;
			this.createImage(files[0]);
			var output = document.getElementById('output');
    		output.src = URL.createObjectURL(files[0]);
		}

	}

	createImage(file) {
		let reader = new FileReader();
		reader.onload = (e) => {
			this.setState({
				avatar: e.target.result
			})
		};
		reader.readAsDataURL(file);
	}

	onSave (event) {
		event.preventDefault();
		this.setState({
			submitted:true
		});
		var {id, username, firstname, lastname, email, role_id, phone, address, is_active, gender, avatar} = this.state;
		var data = { 
			username: username,
			firstname: firstname,
			lastname: lastname,
			email: email,
			role_id: role_id,
			phone: phone,
			address: address,
			gender: gender,
			is_active: is_active ? config.ACTIVED : config.DEACTIVED,
			avatar: avatar 
		};
		let { isFormValidationErrors } = this.state;
        if ( !isFormValidationErrors ){
			if(id) { //update
				this.props.onEditUser(data, id);
			} else { //create
				console.log('ok');
				this.props.onAddUser(data);
			}
        }
	}

	render() {
		if(this.state.loggedOut){
			return <Redirect to={{ pathname: "/"}}/>;
		}

		return (
			<CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
				<div>
					<div className="col-lg-3 col-sm-3 col-xs-3 col-md-3"></div>
					<div className="col-lg-6 col-sm-6 col-xs-6 col-md-6">
						{/* { this.state.isValidation === 'false' ? <ErrorMessage messages={this.props.users}/>: null} */}
						<form noValidate  >
							<legend>Form title</legend>
							<div className="form-group">
								<label>UserName</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.username} 
									onChange={this.onChangeForm} 
									name="username" 
									placeholder="username"/>
								<Validator 
									isValidationError={this.isValidationError}
									isFormSubmitted={this.state.submitted} 
									reference={{username : this.state.username}}
									validationRules={{required:true, minLength: 5,maxLength:10}} 
									validationMessages={{ required: "This field is required", minLength: "Not a valid Min length: 5 ",maxLength: "Not a valid Max length: 10 "}}/>
							</div>
							<div className="form-group">
								<label>FirstName</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.firstname} 
									onChange={this.onChangeForm} 
									name="firstname" 
									placeholder="firstname"/>
								<Validator 
									isValidationError={this.isValidationError}
									isFormSubmitted={this.state.submitted} 
									reference={{firstname : this.state.firstname}}
									validationRules={{required:true, maxLength:50}} 
									validationMessages={{ required: "This field is required", maxLength: "Not a valid Max length: 10 "}}/>
							</div>
							<div className="form-group">
								<label>LastName</label>
								<input 
									type="text" 
									className="form-control" 
									value={this.state.lastname} 
									onChange={this.onChangeForm} 
									name="lastname" 
									placeholder="lastname"/>
								<Validator 
									isValidationError={this.isValidationError}
									isFormSubmitted={this.state.submitted} 
