import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group'
// import { Link } from 'react-router-dom';
import Validator from 'react-forms-validator';
import callApi from './../../utils/apiCaller';
import * as config from '../../constants/config';
import Button from '@material-ui/core/Button';
import Swal from 'sweetalert2';
class MyAccount extends Component {

    constructor (props) {
        super(props)
        this.state = {
            username: '',
            email: '',
            firstname: '',
            lastname: '',
            address: '',
            phone: '',
            gender: '',
            birthday: '',
            isFormValidationErrors : true,
			submitted: false,
        }
    }

    isValidationError = (flag) => {
		this.setState({isFormValidationErrors:flag});
   	}

       onChangeForm = (event) => {
        var target = event.target;
		var name = target.name;
		var value = target.type === 'checkbox'? target.checked:target.value;
		this.setState({
			[name]: value
		});
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
		this.setState({ submitted:true });
		var {old_password, new_password, confirm_password} = this.state;
		var data = { 
			old_password: old_password,
			new_password: new_password,
			confirm_password: confirm_password
		};
		let { isFormValidationErrors } = this.state;
        if ( !isFormValidationErrors ){

			callApi('POST', config.API_URL + "change-password", data).then( res => {
                console.log(res);
                if(res){
                    if( res.data.status ){
                        Swal(' Good job!', res.data.message, 'success')
                    } else {
                        Swal('Oops...', res.data.message, 'error')
                    }
                }
            });
        } else {
            Swal('Oops...', 'Something went wrong!', 'error')

        }
    }

    render() {
        return (
            <React.Fragment>
            <CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
                <div className="grid simple">
                    <div className="grid-title no-border">
                        <h4>My Account</h4>
                    </div>
                    <div className="grid-body no-border">
                        <div class="row">
                            <div class="col-md-3 col-lg-3 user-profile">
                                <div className="user-info-wrapper sm"> 
                                    <div className="profile-wrapper1 sm text-center">
                                        <img src="https://backend.ssllabel.com/images/thumbnails/300_5b3efede24593.jpg"
                                            alt=""
                                            data-src="https://backend.ssllabel.com/images/thumbnails/300_5b3efede24593.jpg"
                                            data-src-retina="https://backend.ssllabel.com/images/thumbnails/300_5b3efede24593.jpg"
                                            width="200"
                                            height="200"/>
                                        <div className="availability-bubble online"></div>
                                    </div>

                                </div>
                                <h3 className="text-center">quandv</h3>
                                <p className="text-center">Quan Duong</p>

                                <ul className="list-unstyled text-center">
                                    <li><p><i className="fa fa-map-marker m-r-xs"></i>   HN</p></li>
                                    <li><p><i className="fa fa-envelope m-r-xs"></i><a href="#"> quandv.125@gmail.com</a></p></li>
                                </ul>

                            </div>
                            <div className="col-md-9 col-lg-9 m-t-lg">
                                <h4 className="bold">Tài khoản  <a href="https://backend.ssllabel.com/admin/users/edit/21"><i className="fa fa-pencil"></i></a> </h4>
                                <div className="progress progress-xs">
                                    <div className="progress-bar progress-bar-success" role="progressbar" aria-valuenow="100" aria-valuemin="0" aria-valuemax="100">
                                    </div>
                                </div>
                                    <form noValidate onSubmit={this.handleFormSubmit}>
                                        <div className="col-lg-6 col-md-6">
                                            <div className="form-group">
                                                <label>UserName</label>
                                                <input 
                                                    type="text" 
                                                    className="form-control" 
                                                    value={this.state.username} 
                                                    onChange={this.onChangeForm} 
                                                    name="username" 
                                                    placeholder="username"/>
                                                <Validator 
                                                    isValidationError={this.isValidationError}
                                                    isFormSubmitted={this.state.submitted} 
                                                    reference={{username : this.state.username}}
                                                    validationRules={{required:true, minLength: 5,maxLength:10}} 
                                                    validationMessages={{ required: "This field is required", minLength: "Not a valid Min length: 5 ",maxLength: "Not a valid Max length: 10 "}}/>
                                            </div>
                                            <div className="form-group">
                                                <label>FirstName</label>
                                                <input 
                                                    type="text" 
                                                    className="form-control" 
                                                    value={this.state.firstname} 
                                                    onChange={this.onChangeForm} 
                                                    name="firstname" 
                                                    placeholder="firstname"/>
                                                <Validator 
                                                    isValidationError={this.isValidationError}
                                                    isFormSubmitted={this.state.submitted} 
                                                    reference={{firstname : this.state.firstname}}
                                                    validationRules={{required:true, maxLength:50}} 
                                                    validationMessages={{ required: "This field is required", maxLength: "Not a valid Max length: 10 "}}/>
                                            </div>

                                            <div className="form-group">
                                                <label>Address</label>
                                                <input 
                                                    type="text" 
                                                    className="form-control" 
                                                    value={this.state.address} 
                                                    onChange={this.onChangeForm} 
                                                    name="address" 
                                                    placeholder="address"/>
                                                <Validator 
                                                    isValidationError={this.isValidationError}
                                                    isFormSubmitted={this.state.submitted} 
