import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group'
import LoginForm from './../../components/Login/LoginForm';
import {Redirect} from 'react-router-dom';
import { connect } from "react-redux";
import * as config from './../../constants/config';

class Login extends Component {

     constructor(props) {
        super(props);
        this.state = {
            isLogin: false
        };
    }
    componentWillReceiveProps(nextprops) {
        if(nextprops.authentication){
            this.setState({
                isLogin: nextprops.authentication 
            });
        }
    }

    render() {
        if(this.state.isLogin){
            return <Redirect to={{ pathname: "/"}}/>;
		}
        return (
            <CSSTransitionGroup transitionName={config.LOGINTRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
              <div className="error-body no-top lazy">
                    <div className="container">
                        <div className="row login-container animated fadeInUp">
                            <div className="col-md-7 col-md-offset-2 tiles white no-padding">
                                <div className="p-t-30 p-l-40 p-b-20 xs-p-t-10 xs-p-l-10 xs-p-b-10">
                                <h2 className="normal">
                                    Sign in to webarch
                                </h2>

                                <p className="p-b-20">
                                    Sign up Now! for webarch accounts, it's free and always will be..
                                </p>
                                <div role="tablist">
                                    <a href="#tab_login" className="btn btn-primary btn-cons" role="tab" data-toggle="tab">Login</a> or&nbsp;&nbsp;
                                    <a href="#tab_register" className="btn btn-info btn-cons" role="tab" data-toggle="tab">Create an account</a>
                                </div>
                                </div>
                                {/* 
                                <div className="col-lg-12 col-sm-12 col-xs-12 col-md-12" >
                                <LoginForm/>
                                </div>
                                */}
                                <div className="tiles grey p-t-20 p-b-20 no-margin text-black tab-content">
                                <div role="tabpanel" className="tab-pane active" id="tab_login">
                                    <form className="animated fadeIn validate">
                                        <div className="row form-row m-l-20 m-r-20 xs-m-l-10 xs-m-r-10">
                                            <div className="col-md-6 col-sm-6">
                                            <input className="form-control" id="login_username" name="login_username" placeholder="Username" type="email" required/>
                                            </div>
                                            <div className="col-md-6 col-sm-6">
                                            <input className="form-control" id="login_pass" name="login_pass" placeholder="Password" type="password" required/>
                                            </div>
                                        </div>
                                        <div className="row p-t-10 m-l-20 m-r-20 xs-m-l-10 xs-m-r-10">
                                            <div className="control-group col-md-10">
                                            <div className="checkbox checkbox check-success">
                                                <a href="#">Trouble login in?</a>&nbsp;&nbsp;
                                                <input id="checkbox1" type="checkbox" value="1"/>
                                                <label >Keep me reminded</label>
                                            </div>
                                            </div>
                                        </div>
                                    </form>
                                </div>
                                <div role="tabpanel" className="tab-pane" id="tab_register">
                                    <form className="animated fadeIn validate">
                                        <div className="row form-row m-l-20 m-r-20 xs-m-l-10 xs-m-r-10">
                                            <div className="col-md-6 col-sm-6">
                                            <input className="form-control" id="reg_username" name="reg_username" placeholder="Username" type="text" required/>
                                            </div>
                                            <div className="col-md-6 col-sm-6">
                                            <input className="form-control" id="reg_pass" name="reg_pass" placeholder="Password" type="password" required/>
                                            </div>
                                        </div>
                                        <div className="row form-row m-l-20 m-r-20 xs-m-l-10 xs-m-r-10">
                                            <div className="col-md-12">
                                            <input className="form-control" id="reg_mail" name="reg_mail" placeholder="Mailing Address" type="email" required/>
                                            </div>
                                        </div>
                                        <div className="row form-row m-l-20 m-r-20 xs-m-l-10 xs-m-r-10">
                                            <div className="col-md-6 col-sm-6">
                                            <input className="form-control" id="reg_first_Name" name="reg_first_Name" placeholder="First Name" type="text" required/>
                                            </div>
                                            <div className="col-md-6 col-sm-6">
                                            <input className="form-control" id="reg_first_Name" name="reg_first_Name" placeholder="Last Name" type="text" required/>
                                            </div>
                                        </div>
                                        <div className="row form-row m-l-20 m-r-20 xs-m-l-10 xs-m-r-10">
                                            <div className="col-md-12">
                                            <input className="form-control" id="reg_email" name="reg_email" placeholder="Email" type="email" required/>
                                            </div>
                                        </div>
                                    </form>
                                </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    </div>
            </CSSTransitionGroup>
        );
    }
}

const mapStateToProps = state => {
    return {
        authentication: state.authentication.status
    }
}
export default connect(mapStateToProps, null)(Login);
