import React, { Component } from 'react';
import UsersList from './../../components/Users/UsersList';
import UserSpec from './../../components/Users/UserSpec';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { actFetchUsersRequest, actDeleteUserRequest } from './../../actions/index';
import { Redirect } from 'react-router-dom';
// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";


class UsersPage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			image: '',
			loggedOut: false
		}
		this.onDelete = this.onDelete.bind(this);
	}

	componentWillReceiveProps(nextprops) {
        if(!nextprops.authentication.loggedOut){
			this.setState({
				loggedOut: nextprops.authentication.loggedOut
			});

		}

    }
	componentWillMount(){
		this.props.getUsers();
	}
	onDelete (id) {
		this.props.onDeleteUser(id)
	}

	render() {
		if(this.state.loggedOut){
			return <Redirect to={{ pathname: "/"}}/>;
		}
		var {users} = this.props.users;


		const data = [{
			username: 'quandv1',
			firstname: 'Tanner ',
			lastname: 'Linsley',
		  },{
			username: 'quandv2',
			firstname: 'Tanner ',
			lastname: 'Linsley',
		  },{
			username: 'quandv',
			firstname: 'Tanner ',
			lastname: 'Linsley',
		  },{
			username: 'quandv',
			firstname: 'Tanner ',
			lastname: 'Linsley',
		  },{
			username: 'quandv',
			firstname: 'Tanner ',
			lastname: 'Linsley',
		  }];
		 console.log(data);
		 console.log(users);
		return (
			<ReactTable
				data={data}
				columns={[
					{

						columns: [
							{
								Header: "UserName",
								accessor: "username"
							},
							{
								Header: "First Name",
								accessor: "firstname"
							},
							{
								Header: "Last Name",
								id: "lastName",
								accessor: "lastname"
							}
						]
					}


          	]}
          defaultPageSize={10}
          className="-striped -highlight"
        />
			// <div className="col-lg-12 col-sm-12 col-xs-12 col-md-12">

			// 	{/* <Link to="/users/add" className="btn btn-primary">
			// 		Add
			// 	</Link>

			// 	<br/><br/>
			// 	<UsersList>
            //         { this.showUser(users) }
			// 	</UsersList> */}
			// </div>
		);
	} // end render

	showUser (users) {
		var result = null;
		if( users ){
			if ( users && typeof users !== 'undefined' && users.length > 0) {
				result = users.map((user, index) => {
					return (<UserSpec key={index} user={user} index={index} onDelete={this.onDelete}/>);
				});
			}
		}
		return result;
	}

}

const mapStateToProps = state => {
	return {
		authentication: state.authentication,
		users: state.users
	}
}

const mapDispatchToProps = (dispatch, props) => {
	return {
		getUsers : () => {
			dispatch(actFetchUsersRequest());
		},
		onDeleteUser : (id) => {
			dispatch(actDeleteUserRequest(id));
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersPage);
