import React, { Component } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Menu from './components/Menu/Menu';
import Login from './pages/Login/Login';
import routes from './routes';
import { ISLOGIN } from './constants/config';
// import { TransitionGroup, CSSTransition } from "react-transition-group";
import { connect } from 'react-redux';
import { spring, AnimatedRoute } from 'react-router-transition';

class App extends Component {

	constructor(props){
		super(props)
		this.state = {
			isLogin : ISLOGIN
		}
	}

	componentWillReceiveProps(nextprops){
		this.setState({
			isLogin: nextprops.authentication.loggedIn
		});		
	}

	render() {

		if( this.state.isLogin ) {
			return (

				<Router>
					 <AnimatedSwitch
    atEnter={bounceTransition.atEnter}
    atLeave={bounceTransition.atLeave}
    atActive={bounceTransition.atActive}
    mapStyles={mapStyles}
    className="route-wrapper"
  >
					<Route render={({ location }) => (
						<div className="App" >
							<Menu />

							<Switch location={location}>

								{this.showContentMenu(routes,location)}

							</Switch>

						</div>
					)}/>
					    </AnimatedSwitch>

				</Router>
			);
		} else {
			return (
				<Router>
					<Route render={({ location }) => (
						<div className="App" >

							<Login />

						</div>
					)}/>
				</Router>
			);
		}

	}

	showContentMenu = (routes,location) => {
		var result = null;
		if(routes.length > 0){
			result = routes.map((route, index) => {
				return (
					<Route 
						key={index} 
						path={route.path} 
						exact={route.exact} 
						component={route.main} 
					/>
				);
			})
		}
		return result;
	}

}

const mapStateToProps = state => {
	return {
		authentication: state.authentication,
	}
}

// const mapDispatchToProps = (dispatch, props) => {
// 	return {
// 		getUsers : () => {
// 			dispatch(actFetchUsersRequest());
// 		},
// 		onDeleteUser : (id) => {
// 			dispatch(actDeleteUserRequest(id));
// 		}
// 	}
// }
// we need to map the `scale` prop we define below
// to the transform style property
function mapStyles(styles) {
	return {
	  opacity: styles.opacity,
	  transform: `scale(${styles.scale})`,
	};
  }

  // wrap the `spring` helper to use a bouncy config
  function bounce(val) {
	return spring(val, {
	  stiffness: 330,
	  damping: 22,
	});
  }

  // child matches will...
  const bounceTransition = {
	// start in a transparent, upscaled state
	atEnter: {
	  opacity: 0,
	  scale: 1.2,
	},
	// leave in a transparent, downscaled state
	atLeave: {
	  opacity: bounce(0),
	  scale: bounce(0.8),
	},
	// and rest at an opaque, normally-scaled state
	atActive: {
	  opacity: bounce(1),
	  scale: bounce(1),
	},
  };
export default connect(mapStateToProps, null)(App);
