import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group'
import * as config from '../../constants/config';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { actFetchBillingRequest, actDeleteBillingRequest } from '../../actions/index';
import Button from '@material-ui/core/Button';
import Swal from 'sweetalert2'

import { Redirect } from 'react-router-dom';

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";
import matchSorter from 'match-sorter';
import { connectIO } from '../../socketIO/client';
import { ToastContainer, toast } from 'react-toastify';
class Billing extends Component {

	constructor(props) {
		super(props);
		this.state = {
			billing : [],
			loggedOut: false,
			role_id: sessionStorage.getItem('authentication') ? JSON.parse(sessionStorage.getItem('authentication')).role_id : '',
			service_id: sessionStorage.getItem('authentication') ? JSON.parse(sessionStorage.getItem('authentication')).service_id : '',
		}

		this.onDelete = this.onDelete.bind(this);
	}

	componentWillReceiveProps(nextprops) {
        this.setState({
            loggedOut: nextprops.authentication.loggedOut
        });
	}

	componentWillMount(){
		if(this.state.role_id && this.state.role_id === config.ASSISTANT){
			connectIO(message => {
				if(String(this.state.service_id) === String(message)) {
					toast.success("Bạn có bệnh nhân khám mới !", { position: "top-right", autoClose: false, hideProgressBar: true,	closeOnClick: true });
				}
			});
		}
		this.props.getbilling();
	}

	onDelete (id) {

		Swal({
            title: 'Bạn có chắc chắn muốn xóa?',
            text: "",
            type: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
			confirmButtonText: 'Có',
			cancelButtonText: 'Không'
          }).then((result) => {
            if (result.value) {
				this.props.onDeletebilling(id)
				Swal('Good job!','You clicked the button!','success')
            }
        })
	}

	render() {
		if(this.state.loggedOut){
			return <Redirect to={{ pathname: "/"}}/>;
		}

		if (this.props.billing !== null) {
			var {billing} = this.props.billing;
		}

		return (
			<CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>

				<div className="grid simple ">
					<div className="grid-body no-border">
						<Link to="/products" className="margin-right20">
							<Button type="submit" className="btn btn-primary btn-cons" variant="contained" color="primary">
							<i className="material-icons">arrow_back</i>
							</Button>	
						</Link>
						<Link to="/billing/add" className="float-right">
							<Button type="submit" className="btn btn-primary btn-cons" variant="contained" color="primary">
								Thêm mới
							</Button>					
						</Link>
						<div className="clearfix"></div><br/>

						{this.showbillingData(billing)}

					</div>
				</div>
				<ToastContainer />
			</CSSTransitionGroup>
		);
	} // end render

	showbillingData = (billing) => {
		if(this.state.role_id === config.MANAGER  || this.state.role_id === config.ADMINISTRATOR) {
			return this.showbillingforManager(billing)
		} else {
			return this.showbilling(billing)
		}

	}

	showbilling (billing) {
		var result = null;
		if ( billing && typeof billing !== 'undefined' && billing.length > 0) {
			return <ReactTable
						getTdProps={( column ) => ({
							onClick: e => {
								if(column.Header !== 'Action'){
									return (<Link to={`billing/1/edit`}>	</Link>);
								}
							}
						})}
						data={billing}
						noDataText="Không tìm thấy kết quả!"
						previousText= 'Trang trước'
						nextText= 'Trang tiếp'
						loadingText= 'Loading...'
						pageText= 'Trang'
						ofText= 'trong	'
						rowsText= ''
						filterable
						defaultFilterMethod={(filter, row) => String(row[filter.id]) === filter.value}
						columns={[
							{
								Header: 'Thông tin',
								columns: [
									{
										Header: "#",
										id: "row",
										filterable: false,
										maxWidth: 100,
										Cell: (row) => {
											return <div>{row.index+1}</div>;
										}
									},

									{
										Header: "Mã giao dịch",
										id: "transaction_id",
										accessor: d => d.transaction_id,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["transaction_id"] }),
										filterAll: true,
										// maxWidth: 600,
										Cell: (row) => {
											return <div className="text-center">
												<Link to={`billing/edit/${row.original.id}`}>
													  {row.original.transaction_id}
												</Link>
											</div>;
										}
									},
									{
										Header: "Khách hàng",
										id: "customer_title",
										accessor: d => d.customer_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["customer_title"] }),
										// maxWidth: 600,
										filterAll: true
									},
									{
										Header: "Bác sỹ",
										id: "user_title",
										accessor: d => d.user_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["user_title"] }),
										// maxWidth: 600,
										filterAll: true
									},
									{
										Header: "Danh mục",
										id: "category_title",
										accessor: d => d.category_title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["category_title"] }),
										// maxWidth: 600,
										filterAll: true
									},
									{
										Header: "Thời gian khám lại",
										id: "time",
										accessor: d => d.time,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["time"] }),
										// maxWidth: 600,
										filterAll: true
									},
								]
							}
					]}
					defaultSorted={[{
						id: "row",
					}]}
					defaultPageSize={5}  
					className="-striped -highlight"
				/>
		}

		return result;
	}

	showbillingforManager (billing) {
		var result = null;
		if ( billing && typeof billing !== 'undefined' && billing.length > 0) {
			console.log(billing);
			return <ReactTable
						getTdProps={( column ) => ({
							onClick: e => {
								if(column.Header !== 'Action'){
									return (<Link to={`billing/1/edit`}>	</Link>);
								}
							}
						})}
						data={billing}
						noDataText="Không tìm thấy kết quả!"
						previousText= 'Trang trước'
						nextText= 'Trang tiếp'
						loadingText= 'Loading...'
						pageText= 'Trang'
						ofText= 'trong	'
						rowsText= ''
						filterable
						defaultFilterMethod={(filter, row) => String(row[filter.id]) === filter.value}
						columns={[
							{
								Header: 'Loại Dịch vụ',
								columns: [
									{
										Header: "#",
										id: "row",
										filterable: false,
										maxWidth: 100,
										Cell: (row) => {
											return <div>{row.index+1}</div>;
										}
									},

									{
										Header: "Tên loại dịch vụ",
										id: "title",
										accessor: d => d.title,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["title"] }),
										filterAll: true,
										maxWidth: 600,
										Cell: (row) => {
											return <div className="text-center">
												<Link to={`billing/edit/${row.original.id}`}>
													  {row.original.title}
												</Link>
											</div>;
										}
									},
									{
										Header: "Mô tả",
										id: "desc",
										accessor: d => d.desc,
										filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["desc"] }),
										maxWidth: 600,
										filterAll: true
									},
									{
										Header: "",
										filterable: false,
										maxWidth: 250,
										Cell: row => (
											<Button type="submit" className="btn btn-primary btn-cons-small" variant="fab" color="secondary" size="small"  onClick={ () => this.onDelete(row.original.id)}>
											<i className="material-icons">delete</i>
											</Button>
										)
									}
								]
							}
					]}
					defaultSorted={[{
						id: "row",
					}]}
					defaultPageSize={20}  
					className="-striped -highlight"
				/>
		}

		return result;
	}
}

