import React, { Component } from 'react';
import * as config from '../../constants/config';
import Button from '@material-ui/core/Button';
import { CSSTransitionGroup } from 'react-transition-group'
import { Link } from 'react-router-dom';
import Remainder from '../../components/Sms/Remainder';
import { connect } from 'react-redux';
import { actFetchCustomersRequest, actDeleteCustomerRequest } from '../../actions/index';

// Import React Table
import ReactTable from "react-table";
// import "react-table/react-table.css";
import matchSorter from 'match-sorter';

class SmsCategory extends Component {
    render() {
        return (
            <React.Fragment>
                <CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
				<div className="grid simple">
					<div className="grid-body no-border">
						<Link to="/" className="margin-right20">
							<Button type="submit" className="btn btn-primary btn-cons" variant="contained" color="primary">
							<i className="material-icons">arrow_back</i>
							</Button>	
						</Link>

						<Link to="/sms/config" className="float-right">
							<Button type="submit" className="btn btn-primary btn-cons" variant="contained" color="primary">
								<i className="material-icons">settings</i>
							</Button>
						</Link>

						<div className="clearfix"></div><br/>

                        { this.showSmsCategory(null) }

					</div>
				</div>


			</CSSTransitionGroup>
            </React.Fragment>
        );
    }

	showSmsCategory (sms) {
		var result = null;
		if( sms ){
			if ( sms && typeof sms !== 'undefined' && sms.length > 0) {
				console.log(sms);
				return <ReactTable
							getTdProps={( column ) => ({
								onClick: e => {
									if(column.Header !== 'Action'){
										console.log('action')
										return (<Link to={`sms/1/edit`}>	</Link>);
									}
								}

							})}
							data={sms}
							filterable
							defaultFilterMethod={(filter, row) =>
								String(row[filter.id]) === filter.value}
							columns={[
								{
									Header: this.renderAddButton(),
									columns: [
										{
											Header: "#",
											id: "row",
											maxWidth: 50,
											filterable: false,
											Cell: (row) => {
												return <div>{row.index+1}</div>;
											}
										},
										{
											Header: "title",
											id: "title",
											width: 100,
											accessor: d => d.title,
											filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["title"] }),
											filterAll: true
										},
										{
											Header: "Description",
											id: "desc",
											width: 100,
											accessor: d => d.desc,
											filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["desc"] }),
											filterAll: true
										},

										{
											Header: "created_at",
											id: "created_at",
											accessor: d => d.created_at,
											filterMethod: (filter, rows) => matchSorter(rows, filter.value, { keys: ["created_at"] }),
											filterAll: true
										},						
										{
											Header: "Action",
											filterable: false,
											Cell: row => (
												<div>
													{/* <Link to={`sms/${row.original.id}/edit`} className="btn btn-success margin-right-10">
														Edit
													</Link> */}
													<button type="button" className="btn btn-danger" onClick={ () => this.onDelete(row.original.id)}><i className="fa fa-trash"></i></button>
												</div>
											)
										}
									]
								}
						]}
						defaultSorted={[
							{
							id: "row",
							//   desc: true
							}
						]}
						defaultPageSize={20}  
						className="-striped -highlight"
					/>
			}
		}
		return result;
	}
}


const mapStateToProps = state => {
	return {
		authentication: state.authentication,
		customers: state.customers
	}
}

const mapDispatchToProps = (dispatch, props) => {
	return {
		getCustomers : () => {
			dispatch(actFetchCustomersRequest());
		},
		onDeleteCustomer : (id) => {
			dispatch(actDeleteCustomerRequest(id));
		}
	}
}

export default connect(null, null)(SmsCategory);
