import React, { Component } from 'react';
import { CSSTransitionGroup } from 'react-transition-group'
// import axios from 'axios';
import { connect } from "react-redux";
import { Redirect } from 'react-router-dom';
import * as config from '../../constants/config';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';
import Radio from '@material-ui/core/Radio';
import MenuItem from '@material-ui/core/MenuItem';

class SmsActionPage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			// image: '',
			name: '',
			// loggedOut: false,
			checkedA: true,
			checkedB: true,
			selectedValue: 'a',
			selectOption: '',

		}
		// this.onFormSubmit = this.onFormSubmit.bind(this)
		// this.onChange = this.onChange.bind(this)
	}

	handleChange = name => event => {
		// Input, checkbox, Radio, Select
		var target = event.target;
		var value = target.type === 'checkbox'? target.checked : target.value;
		this.setState({
			[name]: value
		});
	};

	handleClick = () => {
		console.log(this.state);
	}

	// componentWillReceiveProps(nextprops) {
    //     this.setState({
    //         loggedOut: nextprops.authentication.loggedOut
    //     });
    // }

	render() {
		// if(this.state.loggedOut){
		// 	return <Redirect to={{ pathname: "/"}}/>;
		// }

		return (
			<CSSTransitionGroup transitionName={config.PAGETRANSITION} transitionAppear={true} transitionAppearTimeout={config.TRANSITIONSPEED} transitionEnter={false} transitionLeave={false}>
				<div className="grid simple ">
					<div className="grid-body no-border">
					<div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
						<h2>SMS add</h2>
						<button onClick={this.handleClick}>Click</button>
						<div className="clearfix"></div>
						<TextField
							label="Name"
							value={this.state.name}
							name="input1"
							onChange={this.handleChange('name')}
						/>
					{/* Checkbox */}
						<Checkbox
							checked={this.state.checkedA}
							onChange={this.handleChange('checkedA')}
							value="checkedA"
						/>
						<Checkbox
							checked={this.state.checkedB}
							onChange={this.handleChange('checkedB')}
							value="checkedB"
							color="primary"
						/>
					{/* Radio */}
						<Radio
							checked={this.state.selectedValue === 'a'}
							onChange={this.handleChange('selectedValue')}
							value="a"
							name="radio-button-demo"
							aria-label="A"
						/>

						<Radio
							checked={this.state.selectedValue === 'b'}
							onChange={this.handleChange('selectedValue')}
							value="b"
							name="radio-button-demo"
							aria-label="B"
						/>

						<Radio
							checked={this.state.selectedValue === 'd'}
							onChange={this.handleChange('selectedValue')}
							value="d"
							color="default"
							name="radio-button-demo"
							aria-label="D"
						/>

					{/* Select */}
						<Select
							value={this.state.selectOption}
							onChange={this.handleChange('selectOption')}

						>
							<MenuItem value=""><em>None</em></MenuItem>
							<MenuItem value={10}>Ten</MenuItem>
							<MenuItem value={20}>Twenty</MenuItem>
							<MenuItem value={30}>Thirty</MenuItem>
						</Select>
					</div>    
				</div></div>
			</CSSTransitionGroup>

		)
	}
}

const mapStateToProps = state => {
    return {
        authentication: state.authentication
    }
}


export default connect(mapStateToProps, null)(SmsActionPage);
